export interface EmployeePerformance {
  employeeId: string;
  employeeName: string;
  department: string;
  performanceScore: number;
  goalCompletionRate: number;
  reviewPeriod: string;
  strengths: string[];
  improvementAreas: string[];
  managerFeedback: string;
  peerRatings: number;
  selfAssessmentScore: number;
  lastUpdated: string;
  tenantId: string;
}

export interface PerformanceMetrics {
  averageScore: number;
  topPerformers: EmployeePerformance[];
  improvementNeeded: EmployeePerformance[];
  departmentBreakdown: Record<string, number>;
  trendsData: {
    period: string;
    averageScore: number;
  }[];
}

export interface PerformanceRequest {
  employeeIds: string[];
  dateRange?: {
    startDate: string;
    endDate: string;
  };
  department?: string;
  includeMetrics: boolean;
}

import { pgTable, uuid, varchar, integer, text, timestamp, decimal, boolean } from 'drizzle-orm/pg-core';
import { relations } from 'drizzle-orm';
import { users } from './users';
import { tenants } from './tenants';

export const performanceReviews = pgTable('performance_reviews', {
  id: uuid('id').primaryKey().defaultRandom(),
  employeeId: uuid('employee_id').notNull().references(() => users.id),
  tenantId: uuid('tenant_id').notNull().references(() => tenants.id),
  reviewPeriod: varchar('review_period', { length: 50 }).notNull(),
  performanceScore: decimal('performance_score', { precision: 3, scale: 1 }).notNull(),
  goalCompletionRate: decimal('goal_completion_rate', { precision: 3, scale: 1 }).notNull(),
  strengths: text('strengths').array(),
  improvementAreas: text('improvement_areas').array(),
  managerFeedback: text('manager_feedback'),
  peerRatings: decimal('peer_ratings', { precision: 3, scale: 1 }),
  selfAssessmentScore: decimal('self_assessment_score', { precision: 3, scale: 1 }),
  managerId: uuid('manager_id').references(() => users.id),
  isActive: boolean('is_active').default(true),
  createdAt: timestamp('created_at').defaultNow().notNull(),
  updatedAt: timestamp('updated_at').defaultNow().notNull()
});

export const performanceGoals = pgTable('performance_goals', {
  id: uuid('id').primaryKey().defaultRandom(),
  employeeId: uuid('employee_id').notNull().references(() => users.id),
  tenantId: uuid('tenant_id').notNull().references(() => tenants.id),
  title: varchar('title', { length: 255 }).notNull(),
  description: text('description'),
  targetDate: timestamp('target_date'),
  completionStatus: varchar('completion_status', { length: 20 }).default('pending'),
  progressPercentage: integer('progress_percentage').default(0),
  createdAt: timestamp('created_at').defaultNow().notNull(),
  updatedAt: timestamp('updated_at').defaultNow().notNull()
});

export const performanceReviewsRelations = relations(performanceReviews, ({ one }) => ({
  employee: one(users, {
    fields: [performanceReviews.employeeId],
    references: [users.id]
  }),
  tenant: one(tenants, {
    fields: [performanceReviews.tenantId],
    references: [tenants.id]
  })
}));

import { Router, Request, Response } from 'express';
import { db } from '../db/connection';
import { performanceReviews, users } from '../db/schema';
import { eq, and, inArray, desc } from 'drizzle-orm';
import { authMiddleware } from '../middleware/auth';
import { tenantMiddleware } from '../middleware/tenant';
import { z } from 'zod';

const router = Router();

const performanceRequestSchema = z.object({
  employeeIds: z.array(z.string().uuid()),
  dateRange: z.object({
    startDate: z.string(),
    endDate: z.string()
  }).optional(),
  department: z.string().optional(),
  includeMetrics: z.boolean().default(true)
});

router.get('/employees', authMiddleware, tenantMiddleware, async (req: Request, res: Response) => {
  try {
    const tenantId = req.tenantId;
    const user = req.user;

    if (!user || user.role !== 'SUPERADMIN') {
      return res.status(403).json({ error: 'Insufficient permissions' });
    }

    const validation = performanceRequestSchema.safeParse(req.query);
    if (!validation.success) {
      return res.status(400).json({ error: 'Invalid request parameters', details: validation.error });
    }

    const { employeeIds, includeMetrics } = validation.data;

    const performanceData = await db
      .select({
        employeeId: performanceReviews.employeeId,
        employeeName: users.name,
        department: users.department,
        performanceScore: performanceReviews.performanceScore,
        goalCompletionRate: performanceReviews.goalCompletionRate,
        reviewPeriod: performanceReviews.reviewPeriod,
        strengths: performanceReviews.strengths,
        improvementAreas: performanceReviews.improvementAreas,
        managerFeedback: performanceReviews.managerFeedback,
        peerRatings: performanceReviews.peerRatings,
        selfAssessmentScore: performanceReviews.selfAssessmentScore,
        lastUpdated: performanceReviews.updatedAt,
        tenantId: performanceReviews.tenantId
      })
      .from(performanceReviews)
      .leftJoin(users, eq(performanceReviews.employeeId, users.id))
      .where(
        and(
          eq(performanceReviews.tenantId, tenantId),
          inArray(performanceReviews.employeeId, employeeIds),
          eq(performanceReviews.isActive, true)
        )
      )
      .orderBy(desc(performanceReviews.updatedAt));

    let metrics = null;
    if (includeMetrics && performanceData.length > 0) {
      const scores = performanceData.map(p => parseFloat(p.performanceScore as string));
      const averageScore = scores.reduce((a, b) => a + b, 0) / scores.length;

      const topPerformers = performanceData
        .filter(p => parseFloat(p.performanceScore as string) >= 4.0)
        .slice(0, 5);

      const improvementNeeded = performanceData
        .filter(p => parseFloat(p.performanceScore as string) < 3.0);

      const departmentBreakdown = performanceData.reduce((acc, p) => {
        const dept = p.department || 'Unknown';
        if (!acc[dept]) acc[dept] = [];
        acc[dept].push(parseFloat(p.performanceScore as string));
        return acc;
      }, {} as Record<string, number[]>);

      const departmentAverages = Object.entries(departmentBreakdown).reduce((acc, [dept, scores]) => {
        acc[dept] = scores.reduce((a, b) => a + b, 0) / scores.length;
        return acc;
      }, {} as Record<string, number>);

      metrics = {
        averageScore: Math.round(averageScore * 10) / 10,
        topPerformers,
        improvementNeeded,
        departmentBreakdown: departmentAverages,
        trendsData: [] // Could be populated with historical data
      };
    }

    res.json({
      success: true,
      data: performanceData,
      metrics
    });
  } catch (error) {
    console.error('Performance data fetch failed:', error);
    res.status(500).json({ 
      success: false, 
      error: 'Failed to fetch performance data'
    });
  }
});

import { EmployeePerformance, PerformanceMetrics, PerformanceRequest } from '@/types/performance';

interface PerformanceResponse {
  success: boolean;
  data: EmployeePerformance[];
  metrics?: PerformanceMetrics;
  error?: string;
}

export async function fetchEmployeePerformanceData(
  employeeIds: string[],
  options: Omit<PerformanceRequest, 'employeeIds'> = { includeMetrics: true }
): Promise<PerformanceResponse> {
  try {
    const queryParams = new URLSearchParams();
    employeeIds.forEach(id => queryParams.append('employeeIds', id));

    if (options.dateRange) {
      queryParams.append('startDate', options.dateRange.startDate);
      queryParams.append('endDate', options.dateRange.endDate);
    }

    if (options.department) {
      queryParams.append('department', options.department);
    }

    queryParams.append('includeMetrics', options.includeMetrics.toString());

    const response = await fetch(`/api/performance/employees?${queryParams.toString()}`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json'
      },
      credentials: 'include'
    });

    if (!response.ok) {
      const errorData = await response.json().catch(() => ({ error: 'Network error' }));
      throw new Error(errorData.error || `HTTP ${response.status}`);
    }

    const result = await response.json();
    return result;
  } catch (error) {
    console.error('Failed to fetch performance data:', error);
    return {
      success: false,
      data: [],
      error: error instanceof Error ? error.message : 'Unknown error occurred'
    };
  }
}

