import { pgTable, text, timestamp, boolean, uuid } from 'drizzle-orm/pg-core';
import { createInsertSchema, createSelectSchema } from 'drizzle-zod';
import { z } from 'zod';

export const surveyTokens = pgTable('survey_tokens', {
  id: uuid('id').defaultRandom().primaryKey(),
  token: text('token').notNull().unique(),
  tenantId: uuid('tenant_id').notNull(),
  surveyId: uuid('survey_id').notNull(),
  email: text('email').notNull(),
  isActive: boolean('is_active').default(true).notNull(),
  expiresAt: timestamp('expires_at').notNull(),
  usedAt: timestamp('used_at'),
  createdAt: timestamp('created_at').defaultNow().notNull(),
  updatedAt: timestamp('updated_at').defaultNow().notNull()
});

export const insertSurveyTokenSchema = createInsertSchema(surveyTokens);
export const selectSurveyTokenSchema = createSelectSchema(surveyTokens);

export type SurveyToken = z.infer<typeof selectSurveyTokenSchema>;
export type InsertSurveyToken = z.infer<typeof insertSurveyTokenSchema>;

import { NextRequest, NextResponse } from 'next/server';
import { z } from 'zod';
import { db } from '../../../../../backend/db/connection';
import { surveyTokens } from '../../../../../backend/db/schema/survey';
import { eq, and } from 'drizzle-orm';

const validateTokenSchema = z.object({
  token: z.string().min(1, 'Token is required')
});

export async function POST(request: NextRequest) {
  try {
    const body = await request.json();
    const { token } = validateTokenSchema.parse(body);

    const surveyToken = await db
      .select()
      .from(surveyTokens)
      .where(
        and(
          eq(surveyTokens.token, token),
          eq(surveyTokens.isActive, true)
        )
      )
      .limit(1);

    if (!surveyToken || surveyToken.length === 0) {
      return NextResponse.json(
        { valid: false, error: 'Invalid or expired survey token' },
        { status: 400 }
      );
    }

    const tokenData = surveyToken[0];
    const now = new Date();

    if (tokenData.expiresAt < now) {
      return NextResponse.json(
        { valid: false, error: 'Survey token has expired' },
        { status: 400 }
      );
    }

    if (tokenData.usedAt) {
      return NextResponse.json(
        { valid: false, error: 'Survey token has already been used' },
        { status: 400 }
      );
    }

    return NextResponse.json({
      valid: true,
      tenantId: tokenData.tenantId,
      surveyId: tokenData.surveyId,
      email: tokenData.email
    });

  } catch (error) {
    console.error('Token validation error:', error);

    if (error instanceof z.ZodError) {
      return NextResponse.json(
        { valid: false, error: 'Invalid request format' },
        { status: 400 }
      );
    }

    return NextResponse.json(
      { valid: false, error: 'Internal server error during token validation' },
      { status: 500 }
    );
  }
}

