import { NextRequest, NextResponse } from 'next/server';
import { getServerSession } from 'next-auth';
import { authOptions } from '@/lib/auth';
import { db } from '@/lib/db';
import { tenants, userTenantMemberships, users } from '@/lib/db/schema';
import { eq, and } from 'drizzle-orm';
import { z } from 'zod';

const responseSchema = z.object({
  tenants: z.array(z.object({
    id: z.string(),
    name: z.string(),
    slug: z.string(),
    role: z.enum(['OWNER', 'ADMIN', 'MEMBER'])
  }))
});

export async function GET(request: NextRequest) {
  try {
    const session = await getServerSession(authOptions);

    if (!session?.user?.id) {
      return NextResponse.json(
        { error: 'Unauthorized' }, 
        { status: 401 }
      );
    }

    const userTenants = await db
      .select({
        id: tenants.id,
        name: tenants.name,
        slug: tenants.slug,
        role: userTenantMemberships.role
      })
      .from(tenants)
      .innerJoin(
        userTenantMemberships,
        eq(tenants.id, userTenantMemberships.tenantId)
      )
      .where(
        and(
          eq(userTenantMemberships.userId, session.user.id),
          eq(userTenantMemberships.isActive, true),
          eq(tenants.isActive, true)
        )
      )
      .orderBy(tenants.name);

    const response = {
      tenants: userTenants
    };

    const validatedResponse = responseSchema.parse(response);

    return NextResponse.json(validatedResponse);
  } catch (error) {
    console.error('Failed to fetch user tenants:', error);

    return NextResponse.json(
      { error: 'Failed to fetch tenant data' },
      { status: 500 }
    );
  }
}

import { UserTenant } from '@/types/tenant';

