  tenants: UserTenant[];
  error?: string;
}

export async function fetchUserTenants(): Promise<{
  data: UserTenant[] | null;
  error: string | null;
  isLoading: boolean;
}> {
  try {
    const response = await fetch('/api/user/tenants', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
      credentials: 'include',
    });

    if (!response.ok) {
      if (response.status === 401) {
        return {
          data: null,
          error: 'Please log in to view your organizations',
          isLoading: false
        };
      }

      const errorData = await response.json().catch(() => ({ error: 'Unknown error' }));
      return {
        data: null,
        error: errorData.error || 'Failed to load organizations',
        isLoading: false
      };
    }

    const { tenants } = await response.json();

    return {
      data: tenants,
      error: null,
      isLoading: false
    };
  } catch (error) {
    console.error('Error fetching user tenants:', error);
    return {
      data: null,
      error: 'Network error - please check your connection',
      isLoading: false
    };
  }
}

