  name: varchar('name', { length: 255 }).notNull(),
  slug: varchar('slug', { length: 100 }).notNull().unique(),
  isActive: boolean('is_active').default(true).notNull(),
  createdAt: timestamp('created_at').defaultNow().notNull(),
  updatedAt: timestamp('updated_at').defaultNow().notNull()
});

export const tenantLimits = pgTable('tenant_limits', {
  id: uuid('id').primaryKey().default(sql`gen_random_uuid()`),
  tenantId: uuid('tenant_id').references(() => tenants.id, { onDelete: 'cascade' }).notNull(),
  maxUsers: integer('max_users').default(100).notNull(),
  maxStorageBytes: bigint('max_storage_bytes', { mode: 'number' }).default(5368709120).notNull(), // 5GB default
  maxApiCallsPerDay: integer('max_api_calls_per_day').default(10000).notNull(),
  createdAt: timestamp('created_at').defaultNow().notNull(),
  updatedAt: timestamp('updated_at').defaultNow().notNull()
});

export const tenantUsageLog = pgTable('tenant_usage_log', {
  id: uuid('id').primaryKey().default(sql`gen_random_uuid()`),
  tenantId: uuid('tenant_id').references(() => tenants.id, { onDelete: 'cascade' }).notNull(),
  activeUsers: integer('active_users').notNull(),
  storageUsed: bigint('storage_used', { mode: 'number' }).notNull(),
  apiCallsToday: integer('api_calls_today').notNull(),
  violations: text('violations'),
  checkedAt: timestamp('checked_at').defaultNow().notNull()
});

export const apiUsage = pgTable('api_usage', {
  id: uuid('id').primaryKey().default(sql`gen_random_uuid()`),
  tenantId: uuid('tenant_id').references(() => tenants.id, { onDelete: 'cascade' }).notNull(),
  userId: uuid('user_id'),
  endpoint: varchar('endpoint', { length: 255 }).notNull(),
  method: varchar('method', { length: 10 }).notNull(),
  responseTime: integer('response_time'),
  statusCode: integer('status_code').notNull(),
  createdAt: timestamp('created_at').defaultNow().notNull()
});

export interface TenantUsage {
  activeUsers: number;
  storageUsed: number;
  apiCallsToday: number;
}

export interface TenantLimits {
  maxUsers: number;
  maxStorageBytes: number;
  maxApiCallsPerDay: number;
}

export interface TenantUsageCheck {
  usage: TenantUsage;
  limits: TenantLimits;
  violations: string[];
  isWithinLimits: boolean;
}

export interface TenantUsageLogEntry {
  id: string;
  tenantId: string;
  activeUsers: number;
  storageUsed: number;
  apiCallsToday: number;
