interface StructureReportInput {
  tenantId: string;
  userId: string;
  structureId?: string;
  includeSummary?: boolean;
  includeRecommendations?: boolean;
}

interface StructureReportData {
  organizationName: string;
  reportDate: string;
  structureAnalysis: {
    hierarchy: any[];
    departments: any[];
    reporting: any[];
    insights: string[];
  };
  recommendations: string[];
  metadata: {
    generatedBy: string;
    totalEmployees: number;
    departmentCount: number;
  };
}

export class StructureReportGenerator {
  private knowledgeEngine: KnowledgeEngine;
  private dataEngine: DataEngine;
  private reasoningEngine: ReasoningEngine;

  constructor() {
    this.knowledgeEngine = new KnowledgeEngine();
    this.dataEngine = new DataEngine();
    this.reasoningEngine = new ReasoningEngine();
  }

  async generateReport(input: StructureReportInput): Promise<Buffer> {
    try {
      // 1. KNOWLEDGE ENGINE: Load organizational structure frameworks
      const frameworks = await this.knowledgeEngine.loadFrameworks([
        'organizational-design',
        'span-of-control',
        'hierarchy-optimization'
      ]);

      // 2. DATA ENGINE: Gather and process structure data
      const structureData = await this.gatherStructureData(input.tenantId, input.structureId);
      const enrichedData = await this.dataEngine.processStructureData({
        rawData: structureData,
        frameworks: frameworks,
        context: { tenantId: input.tenantId }
      });

      // 3. REASONING ENGINE: Generate insights and recommendations
      const analysis = await this.reasoningEngine.analyzeStructure({
        enrichedData,
        frameworks,
        analysisType: 'comprehensive'
      });

      // 4. Generate PDF report
      const reportData: StructureReportData = {
        organizationName: enrichedData.organizationName || 'Organization',
        reportDate: new Date().toISOString().split('T')[0],
        structureAnalysis: {
          hierarchy: analysis.hierarchy,
          departments: analysis.departments,
          reporting: analysis.reportingLines,
          insights: analysis.insights
        },
        recommendations: analysis.recommendations,
        metadata: {
          generatedBy: input.userId,
          totalEmployees: enrichedData.totalEmployees || 0,
          departmentCount: enrichedData.departmentCount || 0
        }
      };

      return await this.createPDFDocument(reportData);
    } catch (error) {
      console.error('Structure report generation failed:', error);
      throw new Error(`Failed to generate structure report: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  private async gatherStructureData(tenantId: string, structureId?: string) {
    const structureQuery = db
      .select({
        id: structures.id,
        name: structures.name,
        data: structures.data,
        createdAt: structures.createdAt
      })
      .from(structures)
      .where(eq(structures.tenantId, tenantId));

    if (structureId) {
      structureQuery.where(and(eq(structures.tenantId, tenantId), eq(structures.id, structureId)));
    }

    const structureData = await structureQuery;

    const entriesData = await db
      .select({
        id: entries.id,
        department: entries.department,
        position: entries.position,
        reportingTo: entries.reportingTo,
        level: entries.level
      })
      .from(entries)
      .where(eq(entries.tenantId, tenantId));

    const usersData = await db
      .select({
        id: users.id,
        name: users.name,
        department: users.department,
        role: users.role
      })
      .from(users)
      .where(eq(users.tenantId, tenantId));

    return {
      structures: structureData,
      entries: entriesData,
      users: usersData,
      totalEmployees: usersData.length,
      departmentCount: [...new Set(usersData.map(u => u.department).filter(Boolean))].length
    };
  }

  private async createPDFDocument(data: StructureReportData): Promise<Buffer> {
    return new Promise((resolve, reject) => {
      try {
        const doc = new PDFDocument({ size: 'A4', margin: 50 });
        const buffers: Buffer[] = [];

        doc.on('data', buffers.push.bind(buffers));
        doc.on('end', () => {
          const pdfData = Buffer.concat(buffers);
          resolve(pdfData);
        });

        // Header
        doc.fontSize(24).text('Organizational Structure Report', { align: 'center' });
        doc.fontSize(12).text(`Generated on: ${data.reportDate}`, { align: 'center' });
        doc.text(`Organization: ${data.organizationName}`, { align: 'center' });
        doc.moveDown(2);

        // Executive Summary
        doc.fontSize(16).text('Executive Summary', { underline: true });
        doc.fontSize(11);
        doc.text(`Total Employees: ${data.metadata.totalEmployees}`);
        doc.text(`Department Count: ${data.metadata.departmentCount}`);
        doc.moveDown();

        // Structure Analysis
        doc.fontSize(16).text('Structure Analysis', { underline: true });
        doc.fontSize(11);

        if (data.structureAnalysis.insights.length > 0) {
          doc.text('Key Insights:');
          data.structureAnalysis.insights.forEach((insight, index) => {
            doc.text(`${index + 1}. ${insight}`);
          });
          doc.moveDown();
        }

        // Departments Overview
        if (data.structureAnalysis.departments.length > 0) {
          doc.text('Departments Overview:');
          data.structureAnalysis.departments.forEach((dept: any) => {
            doc.text(` ${dept.name}: ${dept.employeeCount || 0} employees`);
          });
          doc.moveDown();
        }

        // Recommendations
        if (data.recommendations.length > 0) {
          doc.addPage();
          doc.fontSize(16).text('Recommendations', { underline: true });
          doc.fontSize(11);
          data.recommendations.forEach((rec, index) => {
            doc.text(`${index + 1}. ${rec}`);
            doc.moveDown(0.5);
          });
        }

        // Footer
        doc.fontSize(8).text(`Report generated by Mizan Platform on ${new Date().toLocaleString()}`, {
          align: 'center',
          y: doc.page.height - 50
        });

        doc.end();
      } catch (error) {
        reject(error);
      }
    });
  }
}

