  userId: uuid('user_id').notNull().references(() => users.id, { onDelete: 'cascade' }),
  status: executionStatusEnum('status').notNull().default('pending'),
  startTime: timestamp('start_time', { withTimezone: true }).notNull().defaultNow(),
