    references: [tenants.id]
  }),
  user: one(users, {
    fields: [moduleExecutions.userId],
    references: [users.id]
  })
}));

export type ModuleExecution = typeof moduleExecutions.$inferSelect;
export type NewModuleExecution = typeof moduleExecutions.$inferInsert;

