import { db } from '../db/connection';
import { moduleExecutions } from '../db/schema';
import { eq, and, desc, gte, lte, count } from 'drizzle-orm';
import { ModuleExecutionInput, ModuleExecutionUpdate, ModuleExecutionFilter, ModuleExecution } from '../types/moduleExecutions';
import { generateId } from '../lib/utils';

export class ModuleExecutionService {
  async createExecution(
    tenantId: string,
    userId: string,
    input: ModuleExecutionInput
  ): Promise<ModuleExecution> {
    try {
      const [execution] = await db.insert(moduleExecutions).values({
        id: generateId(),
        moduleId: input.moduleId,
        tenantId,
        userId,
        status: 'pending',
        metadata: input.metadata || {},
        createdAt: new Date(),
        updatedAt: new Date()
      }).returning();

      return execution;
    } catch (error) {
      throw new Error(`Failed to create module execution: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  async updateExecution(
    executionId: string,
    tenantId: string,
    update: ModuleExecutionUpdate
  ): Promise<ModuleExecution> {
    try {
      const [execution] = await db.update(moduleExecutions)
        .set({
          ...update,
          endTime: update.status === 'completed' || update.status === 'failed' ? new Date() : undefined,
          updatedAt: new Date()
        })
        .where(and(
          eq(moduleExecutions.id, executionId),
          eq(moduleExecutions.tenantId, tenantId)
        ))
        .returning();

      if (!execution) {
        throw new Error('Module execution not found');
      }

      return execution;
    } catch (error) {
      throw new Error(`Failed to update module execution: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  async getExecution(
    executionId: string,
    tenantId: string
  ): Promise<ModuleExecution | null> {
    try {
      const execution = await db.select()
        .from(moduleExecutions)
        .where(and(
          eq(moduleExecutions.id, executionId),
          eq(moduleExecutions.tenantId, tenantId)
        ))
        .limit(1);

      return execution[0] || null;
    } catch (error) {
      throw new Error(`Failed to get module execution: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  async listExecutions(
    tenantId: string,
    filter: ModuleExecutionFilter = {},
    page: number = 1,
    limit: number = 20
  ): Promise<{ executions: ModuleExecution[]; total: number }> {
    try {
      const conditions = [eq(moduleExecutions.tenantId, tenantId)];

      if (filter.moduleId) {
        conditions.push(eq(moduleExecutions.moduleId, filter.moduleId));
      }
      if (filter.status) {
        conditions.push(eq(moduleExecutions.status, filter.status));
      }
      if (filter.userId) {
        conditions.push(eq(moduleExecutions.userId, filter.userId));
      }
      if (filter.startDate) {
        conditions.push(gte(moduleExecutions.startTime, filter.startDate));
      }
      if (filter.endDate) {
        conditions.push(lte(moduleExecutions.startTime, filter.endDate));
      }

      const whereClause = conditions.length > 1 ? and(...conditions) : conditions[0];
      const offset = (page - 1) * limit;

      const [executions, totalResult] = await Promise.all([
        db.select()
          .from(moduleExecutions)
          .where(whereClause)
          .orderBy(desc(moduleExecutions.createdAt))
          .limit(limit)
          .offset(offset),
        db.select({ count: count() })
          .from(moduleExecutions)
          .where(whereClause)
      ]);

      return {
        executions,
        total: totalResult[0]?.count || 0
      };
    } catch (error) {
      throw new Error(`Failed to list module executions: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  async deleteExecution(
    executionId: string,
    tenantId: string
  ): Promise<boolean> {
    try {
      const result = await db.delete(moduleExecutions)
        .where(and(
          eq(moduleExecutions.id, executionId),
          eq(moduleExecutions.tenantId, tenantId)
        ));

      return result.rowCount > 0;
    } catch (error) {
      throw new Error(`Failed to delete module execution: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }
}
