export async function validateTenantSubscription(tenantId: string): Promise<SubscriptionStatus> {
  try {
    const tenant = await db.select()
      .from(tenantsTable)
      .where(eq(tenantsTable.id, tenantId))
      .limit(1);

    if (!tenant.length) {
      throw new Error(`Tenant not found: ${tenantId}`);
    }

    const subscription = await stripe.subscriptions.retrieve(tenant[0].stripeSubscriptionId);

    return {
      isActive: subscription.status === 'active',
      status: subscription.status,
      currentPeriodEnd: new Date(subscription.current_period_end * 1000),
      planId: subscription.items.data[0]?.price.id || null,
      tenantId
    };
  } catch (error) {
    console.error('Stripe subscription validation error:', error);
    throw new StripeValidationError('Failed to validate tenant subscription', tenantId);
  }
}

import Stripe from 'stripe';
import { db } from '../db/connection';
import { subscriptionsTable, paymentsTable, tenantsTable } from '../db/schema';
import { eq, and } from 'drizzle-orm';
import { randomUUID } from 'crypto';

export class StripeService {
  private stripe: Stripe;

  constructor() {
    if (!process.env.STRIPE_SECRET_KEY) {
      throw new Error('STRIPE_SECRET_KEY environment variable is required');
    }
    this.stripe = new Stripe(process.env.STRIPE_SECRET_KEY, {
      apiVersion: '2023-10-16',
    });
  }

  async createTenantSubscription(tenantId: string, priceId: string, customerId?: string): Promise<Stripe.Subscription> {
    try {
      // Validate tenant exists and get tenant data
      const tenant = await db.select()
        .from(tenantsTable)
        .where(eq(tenantsTable.id, tenantId))
        .limit(1);

      if (!tenant.length) {
        throw new Error(`Tenant ${tenantId} not found`);
      }

      let stripeCustomerId = customerId;

      // Create Stripe customer if not provided
      if (!stripeCustomerId) {
        const customer = await this.stripe.customers.create({
          email: tenant[0].email,
          name: tenant[0].name,
          metadata: {
            tenantId: tenantId,
            platform: 'mizan'
          }
        });
        stripeCustomerId = customer.id;

        // Update tenant with Stripe customer ID
        await db.update(tenantsTable)
          .set({ 
            stripeCustomerId: stripeCustomerId,
            updatedAt: new Date()
          })
          .where(eq(tenantsTable.id, tenantId));
      }

      // Create subscription
      const subscription = await this.stripe.subscriptions.create({
        customer: stripeCustomerId,
        items: [{ price: priceId }],
        metadata: {
          tenantId: tenantId,
          platform: 'mizan'
        },
        expand: ['latest_invoice.payment_intent']
      });

      // Store subscription in database with tenant isolation
      await db.insert(subscriptionsTable).values({
        id: randomUUID(),
        tenantId: tenantId,
        stripeSubscriptionId: subscription.id,
        stripeCustomerId: stripeCustomerId,
        status: subscription.status,
        currentPeriodStart: new Date(subscription.current_period_start * 1000),
        currentPeriodEnd: new Date(subscription.current_period_end * 1000),
        priceId: priceId,
        metadata: JSON.stringify(subscription.metadata),
        createdAt: new Date(),
        updatedAt: new Date()
      });

      return subscription;
    } catch (error) {
      console.error('Error creating tenant subscription:', error);
      throw new Error(`Failed to create subscription for tenant ${tenantId}: ${error.message}`);
    }
  }

  async handleWebhook(event: Stripe.Event): Promise<void> {
    try {
      switch (event.type) {
        case 'invoice.payment_succeeded':
          await this.handlePaymentSucceeded(event.data.object as Stripe.Invoice);
          break;
        case 'invoice.payment_failed':
          await this.handlePaymentFailed(event.data.object as Stripe.Invoice);
          break;
        case 'customer.subscription.updated':
          await this.handleSubscriptionUpdated(event.data.object as Stripe.Subscription);
          break;
        case 'customer.subscription.deleted':
          await this.handleSubscriptionDeleted(event.data.object as Stripe.Subscription);
          break;
        default:
          console.log(`Unhandled event type: ${event.type}`);
      }
    } catch (error) {
      console.error('Error handling webhook:', error);
      throw error;
    }
  }

  private async handlePaymentSucceeded(invoice: Stripe.Invoice): Promise<void> {
    const tenantId = invoice.metadata?.tenantId;
    if (!tenantId) {
      console.error('No tenantId in invoice metadata');
      return;
    }

    // Record payment with tenant isolation
    await db.insert(paymentsTable).values({
      id: randomUUID(),
      tenantId: tenantId,
      stripeInvoiceId: invoice.id,
      stripePaymentIntentId: invoice.payment_intent as string,
      amount: invoice.amount_paid,
      currency: invoice.currency,
      status: 'succeeded',
      paidAt: new Date(invoice.status_transitions.paid_at! * 1000),
      createdAt: new Date(),
      updatedAt: new Date()
    });

    // Update tenant subscription status
    await db.update(subscriptionsTable)
      .set({ 
        status: 'active',
        updatedAt: new Date()
      })
      .where(and(
        eq(subscriptionsTable.tenantId, tenantId),
        eq(subscriptionsTable.stripeSubscriptionId, invoice.subscription as string)
      ));
  }

  private async handlePaymentFailed(invoice: Stripe.Invoice): Promise<void> {
    const tenantId = invoice.metadata?.tenantId;
    if (!tenantId) {
      console.error('No tenantId in invoice metadata');
      return;
    }

    // Record failed payment with tenant isolation
    await db.insert(paymentsTable).values({
      id: randomUUID(),
      tenantId: tenantId,
      stripeInvoiceId: invoice.id,
      stripePaymentIntentId: invoice.payment_intent as string,
      amount: invoice.amount_due,
      currency: invoice.currency,
      status: 'failed',
      failureReason: 'Payment failed',
      createdAt: new Date(),
      updatedAt: new Date()
    });

    // Update subscription status
    await db.update(subscriptionsTable)
      .set({ 
        status: 'past_due',
        updatedAt: new Date()
      })
      .where(and(
        eq(subscriptionsTable.tenantId, tenantId),
        eq(subscriptionsTable.stripeSubscriptionId, invoice.subscription as string)
      ));
  }

  private async handleSubscriptionUpdated(subscription: Stripe.Subscription): Promise<void> {
    const tenantId = subscription.metadata?.tenantId;
    if (!tenantId) {
      console.error('No tenantId in subscription metadata');
      return;
    }

    await db.update(subscriptionsTable)
      .set({
        status: subscription.status,
        currentPeriodStart: new Date(subscription.current_period_start * 1000),
        currentPeriodEnd: new Date(subscription.current_period_end * 1000),
        updatedAt: new Date()
      })
      .where(and(
        eq(subscriptionsTable.tenantId, tenantId),
        eq(subscriptionsTable.stripeSubscriptionId, subscription.id)
      ));
  }

  private async handleSubscriptionDeleted(subscription: Stripe.Subscription): Promise<void> {
    const tenantId = subscription.metadata?.tenantId;
    if (!tenantId) {
      console.error('No tenantId in subscription metadata');
      return;
    }

    await db.update(subscriptionsTable)
      .set({
        status: 'canceled',
        canceledAt: new Date(),
        updatedAt: new Date()
      })
      .where(and(
        eq(subscriptionsTable.tenantId, tenantId),
        eq(subscriptionsTable.stripeSubscriptionId, subscription.id)
      ));
  }

  async getTenantSubscription(tenantId: string): Promise<any> {
    try {
      const subscription = await db.select()
        .from(subscriptionsTable)
        .where(eq(subscriptionsTable.tenantId, tenantId))
        .limit(1);

      return subscription[0] || null;
    } catch (error) {
      console.error('Error getting tenant subscription:', error);
      throw error;
    }
  }

  async cancelTenantSubscription(tenantId: string): Promise<void> {
    try {
      const subscription = await this.getTenantSubscription(tenantId);
      if (!subscription) {
        throw new Error(`No subscription found for tenant ${tenantId}`);
      }

      await this.stripe.subscriptions.cancel(subscription.stripeSubscriptionId);
    } catch (error) {
      console.error('Error canceling tenant subscription:', error);
      throw error;
    }
  }
}

