// Mizan-compliant organizational chart data schema
const orgChartUploadSchema = z.object({
  positions: z.array(z.object({
    id: z.string(),
    title: z.string(),
    department: z.string(),
    reportingTo: z.string().optional(),
    level: z.number(),
    isStrategic: z.boolean().default(false),
    requiredSkills: z.array(z.string()).default([]),
    responsibilities: z.array(z.string()).default([])
  })),
  departments: z.array(z.object({
    id: z.string(),
    name: z.string(),
    function: z.string(),
    headPosition: z.string().optional(),
    strategicImportance: z.number().min(1).max(10).default(5)
  })),
  reportingStructure: z.array(z.object({
    subordinate: z.string(),
    supervisor: z.string(),
    relationshipType: z.enum(['direct', 'dotted', 'matrix']).default('direct')
  }))
});

// Custom request type with tenant isolation and validated body
interface OrgChartUploadRequest extends Request {
  body: z.infer<typeof orgChartUploadSchema>;
  user: {
    id: string;
    tenantId: string;
    role: string;
  };
}

async function handleOrgChartUpload(req: OrgChartUploadRequest, res: Response) {
  try {
    // Validate tenant isolation (should be set by auth middleware)
    if (!req.user?.tenantId) {
      return res.status(401).json({ 
        error: 'Tenant context required',
        code: 'TENANT_ISOLATION_VIOLATION'
      });
    }

    // Strict Zod validation of request body
    const validatedData = orgChartUploadSchema.parse(req.body);

    // Process organizational chart data with tenant isolation
    const structureData = await processOrgChartData(validatedData, req.user.tenantId);

    // Trigger Structure Analysis with Three-Engine Architecture
    const analysisResult = await triggerStructureAnalysis(structureData, req.user.tenantId);

    // Prepare response with analysis triggers
    const response = {
      success: true,
      uploadId: structureData.id,
      analysisId: analysisResult.id,
      triggeredModules: analysisResult.triggeredModules,
      message: 'Organizational chart uploaded and analysis initiated'
    };

    res.status(200).json(response);

  } catch (error) {
    if (error instanceof z.ZodError) {
      return res.status(400).json({
        error: 'Invalid organizational chart data format',
        details: error.errors,
        code: 'VALIDATION_ERROR'
      });
    }

    console.error('Org chart upload error:', error);
    res.status(500).json({
      error: 'Failed to process organizational chart',
      code: 'PROCESSING_ERROR'
    });
  }
}

