// Three-Engine Architecture trigger for Structure Analysis
async function triggerStructureAnalysis(structureData: any, tenantId: string) {
  const analysisId = randomUUID();

  // Initialize Structure Analysis with Three-Engine pattern
  const structureService = new StructureAnalysisService(
    knowledgeEngine,
    dataEngine,
    reasoningEngine
  );

  // Process analysis (will trigger Hiring module if position gaps found)
  const analysis = await structureService.analyze(structureData.data, tenantId);

  // Store analysis results with tenant isolation
  await db.insert(structureAnalysisTable).values({
    id: analysisId,
    tenantId,
    structureId: structureData.id,
    results: JSON.stringify(analysis),
    status: 'completed',
    createdAt: new Date()
  });

  // Check for module triggers
  const triggeredModules = [];

  // Trigger Hiring Module if position gaps identified
  if (analysis.positionGaps && analysis.positionGaps.length > 0) {
    await triggerHiringModule(analysis.positionGaps, tenantId);
    triggeredModules.push('hiring');
  }

  // Trigger Performance Module with departmental structure
  if (analysis.departmentalStructure) {
    await triggerPerformanceModule(analysis.departmentalStructure, tenantId);
    triggeredModules.push('performance');
  }

  return { id: analysisId, triggeredModules };
}

