import { spawn, SpawnOptions } from 'child_process';
import { promisify } from 'util';

interface CommandResult {
  stdout: string;
  stderr: string;
  exitCode: number;
  success: boolean;
}

interface PipelineConfig {
  timeout: number;
  retries: number;
  environment: 'development' | 'staging' | 'production';
}

class MizanPipelineRunner {
  private config: PipelineConfig;

  constructor(config: PipelineConfig) {
    this.config = config;
  }

  /**
   * Execute a command with proper TypeScript typing and error handling
   * Follows Mizan's production-ready standards for automation scripts
   */
  async runCommand(
    command: string, 
    args: string[] = [], 
    options: SpawnOptions = {}
  ): Promise<CommandResult> {
    if (!command || typeof command !== 'string') {
      throw new Error('Command must be a non-empty string');
    }

    if (!Array.isArray(args)) {
      throw new Error('Arguments must be an array of strings');
    }

    // Validate all arguments are strings
    const invalidArgs = args.filter(arg => typeof arg !== 'string');
    if (invalidArgs.length > 0) {
      throw new Error(`All arguments must be strings. Invalid: ${invalidArgs.join(', ')}`);
    }

    return new Promise<CommandResult>((resolve, reject) => {
      const child = spawn(command, args, {
        stdio: 'pipe',
        timeout: this.config.timeout,
        ...options
      });

      let stdout = '';
      let stderr = '';

      child.stdout?.on('data', (data: Buffer) => {
        stdout += data.toString();
      });

      child.stderr?.on('data', (data: Buffer) => {
        stderr += data.toString();
      });

      child.on('close', (code: number | null) => {
        const exitCode = code ?? -1;
        const result: CommandResult = {
          stdout: stdout.trim(),
          stderr: stderr.trim(),
          exitCode,
          success: exitCode === 0
        };

        if (result.success) {
          resolve(result);
        } else {
          reject(new Error(`Command failed with exit code ${exitCode}: ${stderr || stdout}`));
        }
      });

      child.on('error', (error: Error) => {
        reject(new Error(`Failed to execute command: ${error.message}`));
      });

      // Handle timeout
      setTimeout(() => {
        if (!child.killed) {
          child.kill('SIGTERM');
          reject(new Error(`Command timed out after ${this.config.timeout}ms`));
        }
      }, this.config.timeout);
    });
  }

  /**
   * Run multiple commands in sequence with retry logic
   */
  async runCommandSequence(commands: Array<{ command: string; args: string[] }>): Promise<CommandResult[]> {
    const results: CommandResult[] = [];

    for (const { command, args } of commands) {
      let lastError: Error | null = null;
      let success = false;

      for (let attempt = 1; attempt <= this.config.retries; attempt++) {
        try {
          const result = await this.runCommand(command, args);
          results.push(result);
          success = true;
          break;
        } catch (error) {
          lastError = error as Error;
          if (attempt < this.config.retries) {
            console.warn(`Command failed (attempt ${attempt}/${this.config.retries}): ${error}`);
            // Exponential backoff
            await new Promise(resolve => setTimeout(resolve, Math.pow(2, attempt) * 1000));
          }
        }
      }

      if (!success && lastError) {
        throw new Error(`Command failed after ${this.config.retries} attempts: ${lastError.message}`);
      }
    }

    return results;
  }

  /**
   * Validate Mizan platform dependencies and environment
   */
  async validateMizanEnvironment(): Promise<boolean> {
    const requiredCommands = [
      { command: 'node', args: ['--version'] },
      { command: 'npm', args: ['--version'] },
      { command: 'npx', args: ['--version'] }
    ];

    try {
      const results = await this.runCommandSequence(requiredCommands);

      // Validate Node.js version (should be 20.x)
      const nodeVersion = results[0].stdout;
      const majorVersion = parseInt(nodeVersion.replace('v', '').split('.')[0]);

      if (majorVersion < 20) {
        throw new Error(`Node.js version ${nodeVersion} is not supported. Mizan requires Node.js 20.x or higher.`);
      }

      console.log(' Mizan environment validation passed');
      return true;
    } catch (error) {
      console.error(' Mizan environment validation failed:', error);
      return false;
    }
  }
}

