async function runMizanAudit(config: TestConfig): Promise<AuditResult> {
  try {
    const { stdout, stderr } = await execAsync('node scripts/audit-violations.js', {
      cwd: config.projectRoot,
      timeout: 30000
    });

    if (stderr) {
