/**
 * Parse audit output into structured violation results
 */
function parseAuditOutput(output: string): ViolationResult[] {
  const violations: ViolationResult[] = [];
  const lines = output.split('\n').filter(line => line.trim());

  for (const line of lines) {
    try {
      if (line.includes('VIOLATION:')) {
        const violation = parseViolationLine(line);
        if (violation) {
          violations.push(violation);
        }
      }
    } catch (error: unknown) {
      console.warn('Failed to parse violation line:', line);
    }
  }

  return violations;
}

/**
 * Parse individual violation line into structured data
 */
function parseViolationLine(line: string): ViolationResult | null {
  const violationRegex = /File: (.+) Line: (\d+) Rule: (.+) Code: (.+) Description: (.+)/;
  const match = line.match(violationRegex);

  if (!match) {
    return null;
  }

  const [, file, lineStr, rule, code, description] = match;
  const lineNumber = parseInt(lineStr, 10);

  return {
    file: file.trim(),
    line: lineNumber,
    rule: rule.trim(),
    code: code.trim(),
    description: description.trim(),
    severity: determineSeverity(rule.trim()),
    mizanCompliant: isMizanCompliant(rule.trim(), file.trim())
  };
}

/**
 * Determine violation severity based on rule type
 */
function determineSeverity(rule: string): 'low' | 'medium' | 'high' | 'critical' {
  const criticalRules = [
    'TENANT_ISOLATION_MISSING',
    'THREE_ENGINE_VIOLATION',
    'SECURITY_VULNERABILITY'
  ];

  const highRules = [
    'STRICT_TYPESCRIPT_TYPES',
    'ERROR_HANDLING_MISSING',
    'DRIZZLE_ORM_REQUIRED'
  ];

  const mediumRules = [
    'DESIGN_GUIDELINES',
    'FILE_ARCHITECTURE'
  ];

  if (criticalRules.some(r => rule.includes(r))) return 'critical';
  if (highRules.some(r => rule.includes(r))) return 'high';
  if (mediumRules.some(r => rule.includes(r))) return 'medium';

  return 'low';
}

/**
 * Check if violation affects Mizan compliance
 */
function isMizanCompliant(rule: string, file: string): boolean {
  const mizanCriticalRules = [
    'TENANT_ISOLATION',
    'THREE_ENGINE',
    'AGENT_TRIGGERING',
    'MODULE_INTEGRATION'
  ];

  const mizanCriticalFiles = [
    '/ai/engines/',
    '/ai/modules/',
    '/dashboard/culture/',
    '/dashboard/skills/',
    '/dashboard/performance/'
  ];

  const ruleAffectsMizan = mizanCriticalRules.some(r => rule.includes(r));
  const fileAffectsMizan = mizanCriticalFiles.some(f => file.includes(f));

  return !(ruleAffectsMizan || fileAffectsMizan);
}

/**
 * Generate comprehensive audit summary with Mizan metrics
 */
function generateAuditSummary(violations: ViolationResult[], config: TestConfig): AuditSummary {
  const totalViolations = violations.length;
  const criticalViolations = violations.filter(v => v.severity === 'critical').length;

  // Check Mizan-specific compliance
  const tenantIsolationViolations = violations.filter(v => 
    v.rule.includes('TENANT_ISOLATION')
  ).length;

  const threeEngineViolations = violations.filter(v => 
    v.rule.includes('THREE_ENGINE')
  ).length;

  const mizanArchitectureViolations = violations.filter(v => 
    !v.mizanCompliant
  ).length;

  return {
    totalFiles: config.mizanModules.length,
    totalViolations,
    criticalViolations,
    mizanArchitectureCompliance: mizanArchitectureViolations === 0,
    tenantIsolationIntegrity: tenantIsolationViolations === 0,
    threeEnginePatternCompliance: threeEngineViolations === 0
  };
}

