 */
async function validateMizanArchitecture(result: AuditResult, config: TestConfig): Promise<void> {
  console.log(' Validating Mizan Architecture Patterns...');

  const architectureViolations = result.violations.filter(v => 
    v.rule.includes('THREE_ENGINE') || 
    v.rule.includes('AGENT_TRIGGERING') ||
