// Mizan Three-Engine Execution Order
const MIZAN_ENGINE_ORDER = {
  knowledge: 1,
  data: 2,
  reasoning: 3
};

/**
 * Mizan-Intelligent Pipeline Step Executor
 * Maintains Three-Engine Architecture and Agent Triggering workflows
 * Ensures multi-tenant isolation and business logic completeness
 */
export function getExecutableSteps(
  completedSteps: CompletedSteps,
  tenantId: string,
  allPipelineSteps: PipelineStep[]
): ExecutableSteps {
  try {
    // Validate input with Zod schemas
    const validatedCompletedSteps = CompletedStepsSchema.parse(completedSteps);

    // Ensure tenant isolation - filter steps by tenantId
    const tenantCompletedSteps = validatedCompletedSteps.filter(
      step => step.tenantId === tenantId
    );

    const tenantAllSteps = allPipelineSteps.filter(
      step => step.tenantId === tenantId
    );

    // Get completed step IDs for dependency checking
    const completedStepIds = new Set(
      tenantCompletedSteps.map(step => step.id)
    );

    // Find executable steps based on Mizan business logic
    const executableSteps: ExecutableSteps = [];

    for (const step of tenantAllSteps) {
      // Skip if already completed
      if (completedStepIds.has(step.id)) {
        continue;
      }

      // Check if all dependencies are completed
      const dependenciesMet = step.dependencies.every(depId => 
        completedStepIds.has(depId)
      );

      if (dependenciesMet) {
        // Apply Mizan Three-Engine Architecture ordering
        if (step.engineType) {
          const enginePriority = MIZAN_ENGINE_ORDER[step.engineType];
          step.executionOrder = enginePriority;
        }

        // Check for agent triggering requirements
        const triggerConfig = MIZAN_AGENT_TRIGGERS.find(
          trigger => trigger.sourceModule === step.moduleType
        );

        if (triggerConfig) {
          step.agentTriggers = [triggerConfig.targetAgent];
          step.metadata = {
            ...step.metadata,
            triggerConfig,
            dataMapping: triggerConfig.dataMapping
          };
        }

        executableSteps.push(step);
      }
    }

    // Sort by execution order to maintain Mizan workflow integrity
    executableSteps.sort((a, b) => a.executionOrder - b.executionOrder);

    // Validate output
    return ExecutableStepsSchema.parse(executableSteps);

  } catch (error) {
    console.error('Mizan Pipeline Error in getExecutableSteps:', error);
    throw new Error(`Pipeline execution failed for tenant ${tenantId}: ${error.message}`);
  }
}

/**
 * Mizan Agent Trigger Processor
 * Handles CultureRecognition, SkillsLXP, PerformanceTalent/Bonus workflows
 */
export async function processMizanAgentTriggers(
  step: PipelineStep,
  tenantId: string
): Promise<void> {
  if (!step.agentTriggers || step.agentTriggers.length === 0) {
    return;
  }

  for (const agentName of step.agentTriggers) {
    try {
      const triggerData = {
        sourceModule: step.moduleType,
        targetAgent: agentName,
        tenantId,
        stepId: step.id,
        metadata: step.metadata,
        triggeredAt: new Date()
      };

      // Route to appropriate Mizan agent
      switch (agentName) {
        case 'recognition':
          await triggerRecognitionAgent(triggerData);
          break;
        case 'engagement':
          await triggerEngagementAgent(triggerData);
          break;
        case 'lxp':
          await triggerLXPModule(triggerData);
          break;
        case 'hiring':
          await triggerHiringModule(triggerData);
          break;
        case 'talent':
          await triggerTalentModule(triggerData);
          break;
        case 'bonus':
          await triggerBonusModule(triggerData);
          break;
        default:
          console.warn(`Unknown Mizan agent: ${agentName}`);
      }
    } catch (error) {
      console.error(`Failed to trigger ${agentName} agent:`, error);
      throw error;
    }
  }
}

