// Production-ready human review function with complete error handling
export async function humanReview(
  violations: MizanViolation[],
  context: AgentSystemContext
): Promise<HumanReviewResult[]> {
  try {
    // Validate input data with Zod schemas
    const validatedViolations = violations.map(violation => 
      ViolationSchema.parse(violation)
    );

    // Ensure tenant isolation - critical for Mizan Platform
    const tenantIsolatedViolations = validatedViolations.filter(
      violation => violation.tenantId === context.tenantId
    );

    if (tenantIsolatedViolations.length === 0) {
      throw new Error(`No violations found for tenant ${context.tenantId}`);
    }

    // Initialize review results array
    const reviewResults: HumanReviewResult[] = [];

    // Process each violation with Mizan-specific business logic
    for (const violation of tenantIsolatedViolations) {
      const reviewResult = await processViolationReview(violation, context);
      reviewResults.push(reviewResult);
    }

    // Log review completion for audit trail
    console.log(`Human review completed for ${reviewResults.length} violations by agent ${context.agentId}`);

    return reviewResults;

  } catch (error) {
    console.error('Human review process failed:', error);
    throw new Error(`Human review failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

// Helper function to process individual violation reviews
async function processViolationReview(
  violation: MizanViolation,
  context: AgentSystemContext
): Promise<HumanReviewResult> {
  try {
    // Mizan-specific business logic for violation assessment
    const businessCriticality = assessMizanBusinessCriticality(violation);
    const architecturalImpact = assessArchitecturalImpact(violation);

    // Generate review decision based on Mizan Platform priorities
    const decision = generateReviewDecision(violation, businessCriticality, architecturalImpact);

    const reviewResult: HumanReviewResult = {
      violationId: violation.id,
      decision,
      reviewerNotes: generateReviewerNotes(violation, businessCriticality),
      businessJustification: generateBusinessJustification(violation, architecturalImpact),
      recommendedAction: generateRecommendedAction(violation, decision),
      reviewedAt: new Date(),
      reviewedBy: `Agent-${context.agentId}`
    };

    // Validate review result before returning
    return ReviewResultSchema.parse(reviewResult);

  } catch (error) {
    console.error(`Failed to process violation ${violation.id}:`, error);
    throw new Error(`Violation processing failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

// Mizan-specific business criticality assessment
function assessMizanBusinessCriticality(violation: MizanViolation): 'low' | 'medium' | 'high' | 'critical' {
  const { businessImpact } = violation;

  // Critical: Affects core Mizan modules or breaks agent triggering
  if (businessImpact.affectedModules.some(module => 
    ['Culture', 'Structure', 'Skills', 'Performance', 'Hiring'].includes(module)
  ) || !businessImpact.architecturalCompliance.agentTriggering) {
    return 'critical';
  }

  // High: Affects module integration or tenant isolation
  if (!businessImpact.architecturalCompliance.moduleIntegration || 
      !businessImpact.architecturalCompliance.tenantIsolation) {
    return 'high';
  }

  // Medium: Affects Three-Engine pattern
  if (!businessImpact.architecturalCompliance.threeEnginePattern) {
    return 'medium';
  }

  return 'low';
}

// Architectural impact assessment for Mizan Platform
function assessArchitecturalImpact(violation: MizanViolation): {
  severity: string;
  workflowsAffected: number;
  modulesAffected: number;
} {
  return {
    severity: violation.severity,
    workflowsAffected: violation.businessImpact.workflowsBreaking.length,
    modulesAffected: violation.businessImpact.affectedModules.length
  };
}

// Generate review decision based on Mizan Platform priorities
function generateReviewDecision(
  violation: MizanViolation,
  criticality: string,
  impact: { severity: string; workflowsAffected: number; modulesAffected: number }
): 'approve' | 'reject' | 'modify' {
  // Auto-approve critical violations that must be fixed
  if (criticality === 'critical' || impact.workflowsAffected > 0) {
    return 'approve';
  }

  // Modify medium impact violations for optimization
  if (criticality === 'medium' || impact.modulesAffected > 2) {
    return 'modify';
  }

  // Approve low impact violations for consistency
  return 'approve';
}

// Generate detailed reviewer notes
function generateReviewerNotes(
  violation: MizanViolation,
  criticality: string
): string {
  return `Mizan Platform Review - ${criticality.toUpperCase()} Priority

File: ${violation.file}
Rule: ${violation.rule}
Business Impact: ${violation.businessImpact.businessRisk}
Affected Modules: ${violation.businessImpact.affectedModules.join(', ')}
Workflows Breaking: ${violation.businessImpact.workflowsBreaking.length > 0 ? 
  violation.businessImpact.workflowsBreaking.join(', ') : 'None'}

Architectural Compliance:
- Three-Engine Pattern: ${violation.businessImpact.architecturalCompliance.threeEnginePattern ? '' : ''}
- Agent Triggering: ${violation.businessImpact.architecturalCompliance.agentTriggering ? '' : ''}
- Module Integration: ${violation.businessImpact.architecturalCompliance.moduleIntegration ? '' : ''}
- Tenant Isolation: ${violation.businessImpact.architecturalCompliance.tenantIsolation ? '' : ''}`;
}

// Generate business justification
function generateBusinessJustification(
  violation: MizanViolation,
  impact: { severity: string; workflowsAffected: number; modulesAffected: number }
): string {
  if (impact.workflowsAffected > 0) {
    return `Critical fix required to maintain Mizan Platform business workflows. ${impact.workflowsAffected} workflow(s) are currently broken, affecting core platform functionality including agent triggering and module integration.`;
  }

  if (impact.modulesAffected > 2) {
    return `High-priority fix needed to preserve multi-module integration. ${impact.modulesAffected} modules affected, risking strategic alignment and cross-module dependencies.`;
  }

  return `Standard compliance fix to maintain Mizan Platform quality standards and production readiness.`;
}

// Generate recommended action
function generateRecommendedAction(
  violation: MizanViolation,
  decision: 'approve' | 'reject' | 'modify'
): string {
  switch (decision) {
    case 'approve':
      return `Apply fix immediately to restore platform functionality. Ensure Three-Engine Architecture compliance and maintain tenant isolation.`;
    case 'modify':
      return `Apply fix with modifications to optimize for Mizan Platform architecture. Consider additional improvements for module integration.`;
    case 'reject':
      return `Fix not recommended. Consider alternative approach that better aligns with Mizan Platform strategic goals.`;
    default:
      return 'Review decision unclear. Manual intervention required.';
  }
}

