/**
 * Mizan Platform Confidence Calculator
 * Calculates confidence scores for AI agent outputs using Three-Engine Architecture principles
 * Maintains tenant isolation and supports all Mizan analysis types
 */
export function calculateConfidence(input: ConfidenceInput): ConfidenceResult {
  try {
    // Validate input with strict schema
    const validatedInput = confidenceInputSchema.parse(input);

    // Calculate weighted confidence components
    const dataQualityWeight = 0.25;
    const engineConsensusWeight = 0.35;
    const validationWeight = 0.25;
    const businessContextWeight = 0.15;

    // Data Quality Score (0-1)
    const dataQualityScore = validatedInput.dataQuality;

    // Engine Consensus Score (Three-Engine Architecture)
    const engineConsensusScore = calculateEngineConsensus(validatedInput.engineConsensus);

    // Validation Score
    const validationScore = calculateValidationScore(validatedInput.validationResults);

    // Business Context Score
    const businessContextScore = calculateBusinessContextScore(validatedInput.businessContext);

    // Calculate overall confidence with weighted average
    const overallConfidence = (
      (dataQualityScore * dataQualityWeight) +
      (engineConsensusScore * engineConsensusWeight) +
      (validationScore * validationWeight) +
      (businessContextScore * businessContextWeight)
    );

    // Determine confidence level
    const confidenceLevel = determineConfidenceLevel(overallConfidence);

    // Generate risk factors and recommendations
    const riskFactors = identifyRiskFactors(validatedInput, overallConfidence);
    const recommendations = generateRecommendations(validatedInput, overallConfidence);

    return {
      overallConfidence: Math.round(overallConfidence * 100) / 100, // Round to 2 decimal places
      confidenceLevel,
      breakdown: {
        dataQuality: Math.round(dataQualityScore * 100) / 100,
        engineConsensus: Math.round(engineConsensusScore * 100) / 100,
        validation: Math.round(validationScore * 100) / 100,
        businessContext: Math.round(businessContextScore * 100) / 100
      },
      riskFactors,
      recommendations,
      tenantId: validatedInput.tenantId,
      calculatedAt: new Date()
    };

  } catch (error) {
    console.error('Mizan Confidence Calculation Error:', error);
    throw new Error(`Failed to calculate confidence for tenant ${input.tenantId}: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

/**
 * Calculate Three-Engine Architecture consensus score
 */
function calculateEngineConsensus(consensus: EngineConsensus): number {
  const { knowledgeEngineScore, dataEngineScore, reasoningEngineScore, consensusLevel } = consensus;

  // Average of three engines weighted by consensus level
  const engineAverage = (knowledgeEngineScore + dataEngineScore + reasoningEngineScore) / 3;

  // Apply consensus level as a multiplier (higher consensus = higher confidence)
  return engineAverage * consensusLevel;
}

/**
 * Calculate validation score from multiple validators
 */
function calculateValidationScore(validationResults: ValidationResult[]): number {
  if (validationResults.length === 0) return 0;

  let totalScore = 0;
  let criticalIssuesPenalty = 0;
  let warningsPenalty = 0;

  for (const result of validationResults) {
    totalScore += result.score;
    criticalIssuesPenalty += result.criticalIssues * 0.1; // 10% penalty per critical issue
    warningsPenalty += result.warnings * 0.02; // 2% penalty per warning
  }

  const averageScore = totalScore / validationResults.length;
  const penalizedScore = Math.max(0, averageScore - criticalIssuesPenalty - warningsPenalty);

  return penalizedScore;
}

/**
 * Calculate business context score
 */
function calculateBusinessContextScore(context: BusinessContext): number {
  const { industryComplexity, organizationSize, dataCompleteness, strategicAlignment } = context;

  // Organization size factor (larger orgs have more complex analysis)
  const sizeFactors = {
    small: 1.0,
    medium: 0.95,
    large: 0.9,
    enterprise: 0.85
  };

  const sizeFactor = sizeFactors[organizationSize];

  // Weighted average of context factors
  const contextScore = (
    (dataCompleteness * 0.4) +
    (strategicAlignment * 0.3) +
    ((1 - industryComplexity) * 0.3) // Lower complexity = higher confidence
  ) * sizeFactor;

  return Math.min(1, contextScore);
}

/**
 * Determine confidence level from numerical score
 */
function determineConfidenceLevel(score: number): 'low' | 'medium' | 'high' | 'very_high' {
  if (score >= 0.9) return 'very_high';
  if (score >= 0.75) return 'high';
  if (score >= 0.6) return 'medium';
  return 'low';
}

/**
 * Identify risk factors based on input analysis
 */
function identifyRiskFactors(input: ConfidenceInput, confidence: number): string[] {
  const risks: string[] = [];

  if (input.dataQuality < 0.7) {
    risks.push('Low data quality may affect analysis accuracy');
  }

  if (input.engineConsensus.consensusLevel < 0.8) {
    risks.push('Low consensus between AI engines indicates uncertainty');
  }

  const failedValidations = input.validationResults.filter(v => !v.passed).length;
  if (failedValidations > 0) {
    risks.push(`${failedValidations} validation checks failed`);
  }

  if (input.businessContext.dataCompleteness < 0.6) {
    risks.push('Incomplete business data may limit analysis depth');
  }

  if (confidence < 0.6) {
    risks.push('Overall confidence is below recommended threshold');
  }

  return risks;
}

/**
 * Generate recommendations for improving confidence
 */
function generateRecommendations(input: ConfidenceInput, confidence: number): string[] {
  const recommendations: string[] = [];

  if (input.dataQuality < 0.8) {
    recommendations.push('Improve data quality through additional validation and cleaning');
  }

  if (input.engineConsensus.consensusLevel < 0.85) {
    recommendations.push('Review engine parameters and knowledge base alignment');
  }

  if (input.businessContext.dataCompleteness < 0.8) {
    recommendations.push('Collect additional business context data for more accurate analysis');
  }

  if (confidence < 0.75) {
    recommendations.push('Consider running additional validation cycles before finalizing results');
  }

  return recommendations;
}

