/**
 * Scans a file for Mizan platform violations with strict TypeScript compliance
 * Maintains audit system integrity for multi-agent quality assurance
 * 
 * @param filePath - Absolute or relative path to the file to scan
 * @param options - Configuration options for scanning behavior
 * @returns Array of violations found in the file
 * @throws Error if file cannot be read or is invalid
 */
function scanFile(filePath: string, options: ScanOptions = {}): ViolationResult[] {
  try {
    // Validate input parameters with strict typing
    if (typeof filePath !== 'string' || filePath.trim().length === 0) {
      throw new Error('Invalid file path: must be a non-empty string');
    }

    // Get file statistics for validation
    const fileStats: FileStats = getFileStats(filePath);

    if (fileStats.isDirectory) {
      throw new Error(`Path is a directory, not a file: ${filePath}`);
    }

    // Read file content with proper error handling
    const content: string = readFileSync(filePath, 'utf-8');
    const lines: string[] = content.split('\n');
    const violations: ViolationResult[] = [];

    // Determine Mizan module context for business impact assessment
    const mizanModule: string | undefined = determineMizanModule(filePath);

    // Scan each line for violations with strict type checking
    lines.forEach((line: string, index: number) => {
      const lineNumber: number = index + 1;
      const detectedViolations: ViolationResult[] = scanLineForViolations(
        line,
        filePath,
        lineNumber,
        mizanModule
      );
      violations.push(...detectedViolations);
    });

    return violations;

  } catch (error: unknown) {
    const errorMessage: string = error instanceof Error ? error.message : 'Unknown error occurred';
    throw new Error(`Failed to scan file ${filePath}: ${errorMessage}`);
  }
}

/**
 * Gets file statistics with proper error handling and type safety
 */
function getFileStats(filePath: string): FileStats {
  try {
    const stats = statSync(filePath);
    return {
      path: filePath,
      size: stats.size,
      isDirectory: stats.isDirectory(),
      extension: extname(filePath)
    };
  } catch (error: unknown) {
    const errorMessage: string = error instanceof Error ? error.message : 'Unknown error';
    throw new Error(`Cannot access file ${filePath}: ${errorMessage}`);
  }
}

/**
 * Determines which Mizan module a file belongs to for business impact assessment
 */
function determineMizanModule(filePath: string): string | undefined {
  const normalizedPath: string = filePath.toLowerCase().replace(/\\/g, '/');

  const modulePatterns: Record<string, string> = {
    'culture': '/culture/',
    'structure': '/structure/',
    'skills': '/skills/',
    'performance': '/performance/',
    'hiring': '/hiring/',
    'lxp': '/lxp/',
    'talent': '/talent/',
    'bonus': '/bonus/',
    'recognition': '/recognition/',
    'engagement': '/engagement/'
  };

  for (const [module, pattern] of Object.entries(modulePatterns)) {
    if (normalizedPath.includes(pattern)) {
      return module;
    }
  }

  return undefined;
}

/**
 * Scans a single line for violations with Mizan-specific rules
 */
function scanLineForViolations(
  line: string,
  filePath: string,
  lineNumber: number,
  mizanModule?: string
): ViolationResult[] {
  const violations: ViolationResult[] = [];

  // Check for 'any' type violations (critical for Mizan)
  if (line.includes(': any') || line.includes('<any>') || line.includes('any[]')) {
    violations.push({
      file: filePath,
      line: lineNumber,
      rule: 'STRICT_TYPESCRIPT_TYPES',
      code: line.trim(),
      description: 'Use of "any" type violates strict TypeScript requirements',
      severity: 'high',
      mizanModule,
      businessImpact: mizanModule ? `Affects ${mizanModule} module type safety` : 'Reduces type safety'
    });
  }

  // Check for missing tenant isolation in database queries
  if ((line.includes('db.select()') || line.includes('db.insert(') || line.includes('db.update(')) 
      && !line.includes('tenantId')) {
    violations.push({
      file: filePath,
      line: lineNumber,
      rule: 'TENANT_ISOLATION',
      code: line.trim(),
      description: 'Database query missing tenant isolation',
      severity: 'critical',
      mizanModule,
      businessImpact: 'Breaks multi-tenant data security'
    });
  }

  // Check for mock data usage (forbidden in production)
  if (line.includes('mockData') || line.includes('TODO') || line.includes('placeholder')) {
    violations.push({
      file: filePath,
      line: lineNumber,
      rule: 'NO_MOCK_DATA',
      code: line.trim(),
      description: 'Mock data or placeholders not allowed in production code',
      severity: 'high',
      mizanModule,
      businessImpact: 'Compromises production readiness'
    });
  }

  return violations;
}

/**
 * Scans multiple files with batch processing and progress tracking
 */
function scanFiles(filePaths: string[], options: ScanOptions = {}): ScanResult {
  const startTime: number = Date.now();
  let totalViolations: ViolationResult[] = [];
  let totalLines: number = 0;

  for (const filePath of filePaths) {
    try {
      const violations: ViolationResult[] = scanFile(filePath, options);
      totalViolations.push(...violations);

      // Count lines for statistics
      const content: string = readFileSync(filePath, 'utf-8');
      totalLines += content.split('\n').length;
    } catch (error: unknown) {
      console.error(`Error scanning ${filePath}:`, error);
    }
  }

  const executionTime: number = Date.now() - startTime;

  return {
    violations: totalViolations,
    fileCount: filePaths.length,
    linesScanned: totalLines,
    executionTime
  };
}

