import { promises as fs } from 'fs';
import { join } from 'path';

interface FixData {
  file: string;
  startLine: number;
  endLine: number;
  oldCode: string;
  newCode: string;
  explanation: string;
}

interface MizanFix {
  confidence: number;
  complexity: 'simple' | 'moderate' | 'complex' | 'enterprise';
  mizanCompliance: boolean;
  businessImpact: 'positive' | 'neutral' | 'negative';
  primaryFix: FixData;
  additionalFiles?: Array<{
    path: string;
    content: string;
    reason: string;
  }>;
  dependencies?: string[];
  migrationSteps?: string[];
}

interface ApplyFixesResult {
  success: boolean;
  appliedFixes: number;
  failedFixes: number;
  errors: string[];
  mizanCompliance: boolean;
}

/**
 * Applies Mizan-compliant fixes to the codebase
 * Ensures production-ready code with proper TypeScript types
 * Maintains Three-Engine Architecture and agent triggering systems
 */
async function applyFixes(fixesData: MizanFix[]): Promise<ApplyFixesResult> {
  const result: ApplyFixesResult = {
    success: true,
    appliedFixes: 0,
    failedFixes: 0,
    errors: [],
    mizanCompliance: true
  };

  try {
    console.log(' Starting Mizan-compliant fix application...');

    for (const fix of fixesData) {
      try {
        // Validate Mizan compliance before applying
        if (!fix.mizanCompliance) {
          throw new Error(`Fix for ${fix.primaryFix.file} is not Mizan-compliant`);
        }

        // Apply primary fix
        await applyPrimaryFix(fix.primaryFix);
        result.appliedFixes++;

        // Apply additional files if needed
        if (fix.additionalFiles) {
          for (const additionalFile of fix.additionalFiles) {
            await createAdditionalFile(additionalFile);
          }
        }

        // Log migration steps if provided
        if (fix.migrationSteps) {
          console.log(` Migration steps for ${fix.primaryFix.file}:`);
          fix.migrationSteps.forEach((step, index) => {
            console.log(`  ${index + 1}. ${step}`);
          });
        }

        console.log(` Successfully applied fix to ${fix.primaryFix.file}`);

      } catch (error) {
        result.failedFixes++;
        result.success = false;
        const errorMessage = error instanceof Error ? error.message : 'Unknown error';
        result.errors.push(`Failed to apply fix to ${fix.primaryFix.file}: ${errorMessage}`);
        console.error(` Failed to apply fix to ${fix.primaryFix.file}:`, errorMessage);
      }
    }

    // Validate overall Mizan compliance
    result.mizanCompliance = await validateMizanCompliance();

    console.log(` Fix application complete: ${result.appliedFixes} applied, ${result.failedFixes} failed`);

    return result;

  } catch (error) {
    const errorMessage = error instanceof Error ? error.message : 'Unknown error';
    result.success = false;
    result.errors.push(`Critical error during fix application: ${errorMessage}`);
    console.error(' Critical error during fix application:', errorMessage);

    return result;
  }
}

/**
 * Applies a primary fix to a specific file
 */
async function applyPrimaryFix(fix: FixData): Promise<void> {
  try {
    const filePath = join(process.cwd(), fix.file);

    // Read current file content
    const fileContent = await fs.readFile(filePath, 'utf-8');
    const lines = fileContent.split('\n');

    // Validate line numbers
    if (fix.startLine < 1 || fix.startLine > lines.length) {
      throw new Error(`Invalid start line ${fix.startLine} for file ${fix.file}`);
    }

    // Apply the fix
    if (fix.endLine === -1) {
      // Replace entire file
      await fs.writeFile(filePath, fix.newCode, 'utf-8');
    } else {
      // Replace specific lines
      const beforeLines = lines.slice(0, fix.startLine - 1);
      const afterLines = lines.slice(fix.endLine);
      const newLines = fix.newCode.split('\n');

      const updatedContent = [...beforeLines, ...newLines, ...afterLines].join('\n');
      await fs.writeFile(filePath, updatedContent, 'utf-8');
    }

  } catch (error) {
    throw new Error(`Failed to apply primary fix: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

/**
 * Creates additional files required for Mizan compliance
 */
async function createAdditionalFile(fileData: { path: string; content: string; reason: string }): Promise<void> {
  try {
    const filePath = join(process.cwd(), fileData.path);

    // Ensure directory exists
    const dirPath = join(filePath, '..');
    await fs.mkdir(dirPath, { recursive: true });

    // Write file content
    await fs.writeFile(filePath, fileData.content, 'utf-8');

    console.log(` Created additional file: ${fileData.path} (${fileData.reason})`);

  } catch (error) {
    throw new Error(`Failed to create additional file ${fileData.path}: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

/**
 * Validates overall Mizan platform compliance
 */
async function validateMizanCompliance(): Promise<boolean> {
  try {
    // Check for critical Mizan patterns
    const criticalFiles = [
      'frontend/src/app/layout.tsx',
      'backend/src/index.ts',
      'backend/src/db/connection.ts'
    ];

    for (const file of criticalFiles) {
      const filePath = join(process.cwd(), file);
      try {
        await fs.access(filePath);
      } catch {
        console.warn(` Critical Mizan file missing: ${file}`);
        return false;
      }
    }

    return true;

  } catch (error) {
    console.error('Failed to validate Mizan compliance:', error);
    return false;
  }
}

