/**
 * Executes a Mizan AI Agent with proper type safety and error handling
 * Supports Three-Engine Architecture and maintains tenant isolation
 * @param {MizanAgent} agent - The AI agent to execute
 * @param {string} tenantId - Tenant ID for data isolation
 * @param {any} data - Input data for the agent
 * @returns {Promise<MizanAgentResult>} Agent execution result
 */
async function runMizanAgent(agent, tenantId, data) {
  // Validate agent interface compliance
  if (!agent || typeof agent !== 'object') {
    throw new Error('Invalid agent: Must be a valid MizanAgent object');
  }

  if (!agent.run || typeof agent.run !== 'function') {
    throw new Error('Invalid agent: Must implement run() method');
  }

  if (!agent.type || !agent.name) {
    throw new Error('Invalid agent: Must have type and name properties');
  }

  // Validate tenant isolation
  if (!tenantId || typeof tenantId !== 'string') {
    throw new Error('Invalid tenantId: Required for multi-tenant isolation');
  }

  try {
    console.log(`[Mizan Orchestrator] Starting ${agent.type} agent: ${agent.name} for tenant: ${tenantId}`);

    // Execute agent with tenant isolation and error handling
    const result = await agent.run(data, tenantId);

    // Validate result structure
    if (!result || typeof result !== 'object') {
      throw new Error(`Agent ${agent.name} returned invalid result`);
    }

    // Handle agent triggering based on agent type
    if (agent.type === 'culture' && result.triggerAgents) {
      await handleCultureAgentTriggers(result, tenantId);
    }

    if (agent.type === 'skills' && result.triggerModules) {
      await handleSkillsModuleTriggers(result, tenantId);
    }

    if (agent.type === 'structure' && result.triggerHiring) {
      await handleStructureHiringTrigger(result, tenantId);
    }

    console.log(`[Mizan Orchestrator] Completed ${agent.type} agent: ${agent.name}`);

    return {
      success: true,
      agentType: agent.type,
      agentName: agent.name,
      tenantId: tenantId,
      result: result,
      timestamp: new Date().toISOString(),
      triggersExecuted: result.triggerAgents || result.triggerModules || result.triggerHiring || []
    };

  } catch (error) {
    console.error(`[Mizan Orchestrator] Error in ${agent.type} agent ${agent.name}:`, error);

    return {
      success: false,
      agentType: agent.type,
      agentName: agent.name,
      tenantId: tenantId,
      error: error.message,
      timestamp: new Date().toISOString(),
      triggersExecuted: []
    };
  }
}

/**
 * Handles Culture Agent triggers for Recognition and Engagement agents
 * @param {CultureAnalysisResult} result - Culture analysis result
 * @param {string} tenantId - Tenant ID for isolation
 */
async function handleCultureAgentTriggers(result, tenantId) {
  if (!result.triggerAgents || !Array.isArray(result.triggerAgents)) {
    return;
  }

  for (const trigger of result.triggerAgents) {
    try {
      if (trigger.type === 'recognition') {
        console.log(`[Mizan Orchestrator] Triggering Recognition Agent for tenant: ${tenantId}`);
        const recognitionAgent = await loadAgent('recognition');
        await runMizanAgent(recognitionAgent, tenantId, trigger.data);
      }

      if (trigger.type === 'engagement') {
        console.log(`[Mizan Orchestrator] Triggering Engagement Agent for tenant: ${tenantId}`);
        const engagementAgent = await loadAgent('engagement');
        await runMizanAgent(engagementAgent, tenantId, trigger.data);
      }
    } catch (error) {
      console.error(`[Mizan Orchestrator] Failed to trigger ${trigger.type} agent:`, error);
    }
  }
}

/**
 * Handles Skills Analysis triggers for LXP, Talent, and Bonus modules
 * @param {SkillsAnalysisResult} result - Skills analysis result
 * @param {string} tenantId - Tenant ID for isolation
 */
async function handleSkillsModuleTriggers(result, tenantId) {
  if (!result.triggerModules || !Array.isArray(result.triggerModules)) {
    return;
  }

  for (const trigger of result.triggerModules) {
    try {
      if (trigger.type === 'lxp' && trigger.data.skillsGaps) {
        console.log(`[Mizan Orchestrator] Triggering LXP Module for tenant: ${tenantId}`);
        await triggerLXPModule(trigger.data, tenantId);
      }

      if (trigger.type === 'talent' && trigger.data.skillsData) {
        console.log(`[Mizan Orchestrator] Triggering Talent Module for tenant: ${tenantId}`);
        await triggerTalentModule(trigger.data, tenantId);
      }

      if (trigger.type === 'bonus' && trigger.data.skillsAchievements) {
        console.log(`[Mizan Orchestrator] Triggering Bonus Module for tenant: ${tenantId}`);
        await triggerBonusModule(trigger.data, tenantId);
      }
    } catch (error) {
      console.error(`[Mizan Orchestrator] Failed to trigger ${trigger.type} module:`, error);
    }
  }
}

/**
 * Handles Structure Analysis triggers for Hiring module
 * @param {StructureAnalysisResult} result - Structure analysis result
 * @param {string} tenantId - Tenant ID for isolation
 */
async function handleStructureHiringTrigger(result, tenantId) {
  if (!result.triggerHiring || !result.positionRequirements) {
    return;
  }

  try {
    console.log(`[Mizan Orchestrator] Triggering Hiring Module for tenant: ${tenantId}`);
    const hiringAgent = await loadAgent('hiring');
    await runMizanAgent(hiringAgent, tenantId, {
      positionRequirements: result.positionRequirements,
      reportingStructure: result.reportingStructure,
      teamComposition: result.teamComposition
    });
  } catch (error) {
    console.error(`[Mizan Orchestrator] Failed to trigger hiring module:`, error);
  }
}

