}

interface MizanExecutionResults {
  executionId: string;
  tenantId: string;
  startTime: Date;
  endTime: Date;
  totalDuration: number;
  overallStatus: 'success' | 'partial_success' | 'failed';

  // Core Analysis Results
  agents: {
    structure?: MizanAgentResult;
    culture?: MizanAgentResult;
    skills?: MizanAgentResult;
    performance?: MizanAgentResult;
    hiring?: MizanAgentResult;
    recognition?: MizanAgentResult;
    engagement?: MizanAgentResult;
  };

  // Business Module Results
  modules: {
    lxp?: MizanModuleResult[];
    talent?: MizanModuleResult;
    bonus?: MizanModuleResult;
  };

  // Integration & Workflow Data
  triggers: MizanTrigger[];
  workflowsCompleted: string[];
  workflowsFailed: string[];

  // Business Metrics
  businessMetrics: {
    employeesAnalyzed: number;
    departmentsProcessed: number;
    skillsGapsIdentified: number;
    learningPathsCreated: number;
    talentPoolSize: number;
    bonusesCalculated: number;
  };

  // Quality & Compliance
  dataQuality: {
    completeness: number;
    accuracy: number;
    consistency: number;
  };

  tenantIsolationVerified: boolean;
  errors: string[];
  warnings: string[];
}

interface MizanExecutionSummary {
  executionId: string;
  tenantId: string;
  timestamp: Date;
  duration: string;
  status: 'success' | 'partial_success' | 'failed';

  summary: {
    totalAgentsExecuted: number;
    totalModulesActivated: number;
    totalTriggersProcessed: number;
    overallSuccessRate: number;
  };

  agentSummary: {
    [K in keyof MizanExecutionResults['agents']]: {
      status: string;
      confidence: number;
      keyInsights: string[];
      triggersGenerated: number;
    };
  };

  moduleSummary: {
    [K in keyof MizanExecutionResults['modules']]: {
      status: string;
      itemsProcessed: number;
      businessOutcomes: string[];
    };
  };

  businessImpact: {
    strategicAlignment: number;
    workflowCompleteness: number;
    dataIntegrity: number;
    tenantIsolation: boolean;
  };

  recommendations: string[];
  nextActions: string[];
  criticalIssues: string[];
}

function generateMizanExecutionSummary(results: MizanExecutionResults): MizanExecutionSummary {
  try {
    // Validate tenant isolation
    if (!results.tenantIsolationVerified) {
      throw new Error(`Tenant isolation not verified for execution ${results.executionId}`);
    }

    // Calculate execution duration
    const duration = formatDuration(results.totalDuration);

    // Calculate success rates
    const totalAgents = Object.keys(results.agents).length;
    const successfulAgents = Object.values(results.agents).filter(
      agent => agent?.status === 'completed'
    ).length;
    const overallSuccessRate = totalAgents > 0 ? (successfulAgents / totalAgents) * 100 : 0;

    // Generate agent summaries
    const agentSummary: MizanExecutionSummary['agentSummary'] = {};
    Object.entries(results.agents).forEach(([key, agent]) => {
      if (agent) {
        agentSummary[key as keyof MizanExecutionResults['agents']] = {
          status: agent.status,
          confidence: agent.confidence,
          keyInsights: agent.insights.slice(0, 3), // Top 3 insights
          triggersGenerated: agent.triggers?.length || 0
        };
      }
    });

    // Generate module summaries
    const moduleSummary: MizanExecutionSummary['moduleSummary'] = {};
    Object.entries(results.modules).forEach(([key, moduleData]) => {
      if (moduleData) {
        if (Array.isArray(moduleData)) {
          // Handle LXP module (array of results)
          moduleSummary[key as keyof MizanExecutionResults['modules']] = {
            status: moduleData.every(m => m.status === 'completed') ? 'completed' : 'partial',
            itemsProcessed: moduleData.length,
            businessOutcomes: moduleData.flatMap(m => m.businessOutcomes)
          };
        } else {
          // Handle single module results
          moduleSummary[key as keyof MizanExecutionResults['modules']] = {
            status: moduleData.status,
            itemsProcessed: 1,
            businessOutcomes: moduleData.businessOutcomes
          };
        }
      }
    });

    // Calculate business impact metrics
    const businessImpact = {
      strategicAlignment: calculateStrategicAlignment(results),
      workflowCompleteness: calculateWorkflowCompleteness(results),
      dataIntegrity: (results.dataQuality.completeness + results.dataQuality.accuracy + results.dataQuality.consistency) / 3,
      tenantIsolation: results.tenantIsolationVerified
    };

    // Generate recommendations and next actions
    const recommendations = generateRecommendations(results);
    const nextActions = generateNextActions(results);
    const criticalIssues = identifyCriticalIssues(results);

    return {
      executionId: results.executionId,
      tenantId: results.tenantId,
      timestamp: new Date(),
      duration,
      status: results.overallStatus,

      summary: {
        totalAgentsExecuted: totalAgents,
        totalModulesActivated: Object.keys(results.modules).length,
        totalTriggersProcessed: results.triggers.length,
        overallSuccessRate
      },

      agentSummary,
      moduleSummary,
      businessImpact,
      recommendations,
      nextActions,
      criticalIssues
    };

  } catch (error) {
    console.error('Error generating Mizan execution summary:', error);
    throw new Error(`Failed to generate execution summary: ${error.message}`);
  }
}

// Helper functions for business logic calculations
function formatDuration(milliseconds: number): string {
  const seconds = Math.floor(milliseconds / 1000);
  const minutes = Math.floor(seconds / 60);
  const hours = Math.floor(minutes / 60);

  if (hours > 0) return `${hours}h ${minutes % 60}m ${seconds % 60}s`;
  if (minutes > 0) return `${minutes}m ${seconds % 60}s`;
  return `${seconds}s`;
}

function calculateStrategicAlignment(results: MizanExecutionResults): number {
  // Calculate based on successful agent executions and trigger completions
  const agentSuccess = Object.values(results.agents).filter(a => a?.status === 'completed').length;
  const totalAgents = Object.values(results.agents).filter(a => a !== undefined).length;
  const triggerSuccess = results.triggers.filter(t => t.status === 'completed').length;
  const totalTriggers = results.triggers.length;

  const agentScore = totalAgents > 0 ? (agentSuccess / totalAgents) * 50 : 0;
  const triggerScore = totalTriggers > 0 ? (triggerSuccess / totalTriggers) * 50 : 50;

  return Math.round(agentScore + triggerScore);
}

function calculateWorkflowCompleteness(results: MizanExecutionResults): number {
  const completedWorkflows = results.workflowsCompleted.length;
  const totalWorkflows = results.workflowsCompleted.length + results.workflowsFailed.length;

  return totalWorkflows > 0 ? Math.round((completedWorkflows / totalWorkflows) * 100) : 100;
}

function generateRecommendations(results: MizanExecutionResults): string[] {
  const recommendations: string[] = [];

  // Check for failed agents
  Object.entries(results.agents).forEach(([agentType, agent]) => {
    if (agent?.status === 'failed') {
      recommendations.push(`Investigate ${agentType} agent failure and retry analysis`);
    }
  });

  // Check for low confidence scores
  Object.values(results.agents).forEach(agent => {
    if (agent && agent.confidence < 0.7) {
      recommendations.push(`Review data quality for ${agent.agentType} analysis (low confidence: ${Math.round(agent.confidence * 100)}%)`);
    }
  });

  // Check for failed triggers
  const failedTriggers = results.triggers.filter(t => t.status === 'failed');
  if (failedTriggers.length > 0) {
    recommendations.push(`Resolve ${failedTriggers.length} failed module triggers to complete business workflows`);
  }

  return recommendations;
}

function generateNextActions(results: MizanExecutionResults): string[] {
  const actions: string[] = [];

  // LXP module actions
  if (results.modules.lxp && results.modules.lxp.length > 0) {
    actions.push(`Monitor ${results.modules.lxp.length} learning path assignments and track employee progress`);
  }

  // Talent module actions
  if (results.modules.talent?.status === 'completed') {
    actions.push('Review 9-box talent distribution and implement development plans');
  }

  // Bonus module actions
  if (results.modules.bonus?.status === 'completed') {
    actions.push('Review calculated bonuses and proceed with admin approval workflow');
  }

  // Performance follow-ups
  if (results.agents.performance?.status === 'completed') {
    actions.push('Schedule performance calibration meetings and goal setting sessions');
  }

  return actions;
}

function identifyCriticalIssues(results: MizanExecutionResults): string[] {
  const issues: string[] = [];

  // Tenant isolation issues
  if (!results.tenantIsolationVerified) {
    issues.push('CRITICAL: Tenant isolation not verified - data security risk');
  }

  // Core agent failures
  if (results.agents.culture?.status === 'failed') {
    issues.push('CRITICAL: Culture analysis failed - impacts Recognition and Engagement agents');
  }

  if (results.agents.skills?.status === 'failed') {
    issues.push('CRITICAL: Skills analysis failed - LXP module cannot be activated');
  }

  // Data quality issues
  if (results.dataQuality.completeness < 0.8) {
    issues.push('CRITICAL: Data completeness below 80% - analysis reliability compromised');
  }

  return issues;
}

