/**
 * Production-ready violation filter with Mizan business logic
 * Filters violations based on Agent 1 analysis and Mizan platform requirements
 * 
 * @param violations - Array of code violations detected
 * @param agent1Analyses - Array of Agent 1 analysis results
 * @param config - Optional filter configuration
 * @returns Filtered and prioritized violations for processing
 */
export function filterByAgent1Analysis(
  violations: Violation[],
  agent1Analyses: Agent1Analysis[],
  config: Partial<MizanFilterConfig> = {}
): FilteredViolation[] {
  try {
    const filterConfig: MizanFilterConfig = {
      ...DEFAULT_MIZAN_CONFIG,
      ...config
    };

    // Create analysis lookup map for O(1) access
    const analysisMap = new Map<string, Agent1Analysis>();
    agent1Analyses.forEach(analysis => {
      analysisMap.set(analysis.violationId, analysis);
    });

    // Filter and process violations
    const filteredViolations: FilteredViolation[] = violations
      .map(violation => {
        const analysis = analysisMap.get(violation.id);
        if (!analysis) {
          return null; // Skip violations without analysis
        }

        return {
          ...violation,
          analysis,
          filterReason: '',
          priorityScore: 0,
          processingOrder: 0
        };
      })
      .filter((item): item is FilteredViolation => item !== null)
      .filter(violation => applyMizanFilters(violation, filterConfig))
      .map(violation => calculatePriority(violation, filterConfig))
      .sort((a, b) => b.priorityScore - a.priorityScore)
      .map((violation, index) => ({
        ...violation,
        processingOrder: index + 1
      }));

    // Log filtering results for audit trail
    logFilteringResults(violations.length, filteredViolations.length, filterConfig);

    return filteredViolations;

  } catch (error) {
    console.error('Error in filterByAgent1Analysis:', error);
    throw new Error(`Mizan violation filtering failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

/**
 * Apply Mizan-specific filtering logic
 */
function applyMizanFilters(
  violation: FilteredViolation,
  config: MizanFilterConfig
): boolean {
  const { analysis } = violation;

  // Filter 1: Minimum confidence threshold
  if (analysis.confidence < config.minimumConfidence) {
    violation.filterReason = `Low confidence: ${analysis.confidence} < ${config.minimumConfidence}`;
    return false;
  }

  // Filter 2: Must be real violation
  if (!analysis.isRealViolation) {
    violation.filterReason = 'Agent 1 determined this is not a real violation';
    return false;
  }

  // Filter 3: Exclude non-production files
  if (config.excludePatterns.some(pattern => violation.file.includes(pattern))) {
    violation.filterReason = `File matches exclude pattern: ${violation.file}`;
    return false;
  }

  // Filter 4: Tenant isolation requirement for database operations
  if (config.tenantIsolationRequired && 
      violation.code.includes('db.') && 
      !analysis.architecturalCompliance.tenantIsolation) {
    violation.filterReason = 'Database operation missing tenant isolation';
    return true; // Keep for fixing, but mark as critical
  }

  // Filter 5: Recommendation must be actionable
  if (analysis.recommendation === 'REJECT') {
    violation.filterReason = `Agent 1 recommendation: ${analysis.recommendation}`;
    return false;
  }

  violation.filterReason = 'Passed all Mizan filters';
  return true;
}

/**
 * Calculate priority score based on Mizan business impact
 */
function calculatePriority(
  violation: FilteredViolation,
  config: MizanFilterConfig
): FilteredViolation {
  let score = 0;
  const { analysis } = violation;

  // Base score from confidence
  score += analysis.confidence * 20;

  // Severity multiplier
  const severityMultiplier = {
    'critical': 40,
    'high': 30,
    'medium': 20,
    'low': 10
  };
  score += severityMultiplier[analysis.mizanBusinessImpact.severity];

  // Critical module bonus
  const affectedCriticalModules = analysis.mizanBusinessImpact.affectedModules
    .filter(module => config.criticalModules.includes(module));
  score += affectedCriticalModules.length * 10;

  // Priority workflow bonus
  const affectedPriorityWorkflows = analysis.mizanBusinessImpact.workflowsBreaking
    .filter(workflow => config.priorityWorkflows.some(priority => workflow.includes(priority)));
  score += affectedPriorityWorkflows.length * 15;

  // Architecture compliance penalty
  if (!analysis.architecturalCompliance.threeEnginePattern) score -= 5;
  if (!analysis.architecturalCompliance.agentTriggering) score -= 5;
  if (!analysis.architecturalCompliance.moduleIntegration) score -= 5;
  if (!analysis.architecturalCompliance.tenantIsolation) score += 20; // Critical for multi-tenant

  // Production readiness bonus
  if (!analysis.technicalAssessment.productionReadiness) score += 15;

  // Strategic alignment impact
  if (analysis.strategicAlignment.scalabilityImpact === 'negative') score += 10;
  if (!analysis.strategicAlignment.maintainsWorkflows) score += 15;

  violation.priorityScore = Math.max(0, Math.min(100, score));
  return violation;
}

/**
 * Log filtering results for audit and monitoring
 */
function logFilteringResults(
  totalViolations: number,
  filteredCount: number,
  config: MizanFilterConfig
): void {
  const filteringStats = {
    timestamp: new Date().toISOString(),
    totalViolations,
    filteredCount,
    filterRate: ((filteredCount / totalViolations) * 100).toFixed(2),
    config: {
      minimumConfidence: config.minimumConfidence,
      criticalModulesCount: config.criticalModules.length,
      priorityWorkflowsCount: config.priorityWorkflows.length
    }
  };

  console.log('Mizan Violation Filtering Results:', JSON.stringify(filteringStats, null, 2));
}

