/**
 * Filters and analyzes code violations with Mizan platform-specific intelligence
 * Prioritizes violations that impact business workflows and architectural compliance
 */
async function filterViolations(
  rawViolations: unknown[],
  options: {
    includeLowSeverity?: boolean;
    mizanModulesOnly?: boolean;
    businessCriticalOnly?: boolean;
  } = {}
): Promise<FilterResult> {
  try {
    // Validate input violations against schema
    const validatedViolations: CodeViolation[] = [];

    for (const violation of rawViolations) {
      try {
        const parsed = violationSchema.parse(violation);
        validatedViolations.push(parsed);
      } catch (validationError) {
        console.warn('Invalid violation format, skipping:', validationError);
        continue;
      }
    }

    // Apply Mizan-specific filtering logic
    let filteredViolations = validatedViolations;

    // Filter by severity if requested
    if (!options.includeLowSeverity) {
      filteredViolations = filteredViolations.filter(v => v.severity !== 'low');
    }

    // Filter by Mizan modules if requested
    if (options.mizanModulesOnly) {
      filteredViolations = filteredViolations.filter(v => v.mizanModule !== undefined);
    }

    // Filter by business critical impact if requested
    if (options.businessCriticalOnly) {
      filteredViolations = filteredViolations.filter(v => 
        v.businessImpact.affectedWorkflows.length > 0 ||
        v.businessImpact.tenantIsolationRisk ||
        !v.businessImpact.architectureCompliance
      );
    }

    // Analyze business impact across all violations
    const workflowsAtRisk = new Set<string>();
    const modulesAffected = new Set<string>();
    let tenantIsolationViolations = 0;
    let architectureViolations = 0;

    filteredViolations.forEach(violation => {
      violation.businessImpact.affectedWorkflows.forEach(workflow => 
        workflowsAtRisk.add(workflow)
      );

      if (violation.mizanModule) {
        modulesAffected.add(violation.mizanModule);
      }

      if (violation.businessImpact.tenantIsolationRisk) {
        tenantIsolationViolations++;
      }

      if (!violation.businessImpact.architectureCompliance) {
        architectureViolations++;
      }
    });

    // Count violations by severity
    const severityCounts = filteredViolations.reduce(
      (counts, violation) => {
        counts[`${violation.severity}Count`]++;
        return counts;
      },
      { criticalCount: 0, highCount: 0, mediumCount: 0, lowCount: 0 }
    );

    // Count Mizan-specific violations
    const mizanSpecificCount = filteredViolations.filter(v => 
      v.rule.category === 'mizan-specific'
    ).length;

    const result: FilterResult = {
      totalViolations: validatedViolations.length,
      filteredViolations,
      ...severityCounts,
      mizanSpecificCount,
      businessImpactAnalysis: {
        workflowsAtRisk: Array.from(workflowsAtRisk),
        modulesAffected: Array.from(modulesAffected),
        tenantIsolationViolations,
        architectureViolations
      }
    };

    return result;

  } catch (error) {
    console.error('Error filtering violations:', error);
    throw new Error(`Violation filtering failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

/**
 * Analyzes violation impact on specific Mizan business workflows
 */
function analyzeWorkflowImpact(violation: CodeViolation): string[] {
  const affectedWorkflows: string[] = [];

  // Check for Culture  Recognition/Engagement agent workflow impact
  if (violation.file.includes('culture') || violation.mizanModule === 'culture') {
    affectedWorkflows.push('Culture Analysis  Recognition Agent Triggering');
    affectedWorkflows.push('Culture Analysis  Engagement Agent Triggering');
  }

  // Check for Skills  LXP workflow impact
  if (violation.file.includes('skills') || violation.mizanModule === 'skills') {
    affectedWorkflows.push('Skills Analysis  LXP Module Activation');
    affectedWorkflows.push('Skills Gap Analysis  Learning Path Creation');
  }

  // Check for Performance module workflow impact
  if (violation.file.includes('performance') || violation.mizanModule === 'performance') {
    affectedWorkflows.push('Performance Goal Setting  Culture/Skills Integration');
    affectedWorkflows.push('Performance Evaluation  Talent/Bonus Module Triggering');
  }

  // Check for tenant isolation impact
  if (violation.rule.id === 'MIZAN_TENANT_ISOLATION') {
    affectedWorkflows.push('Multi-tenant Data Isolation');
  }

  return affectedWorkflows;
}

