  errors: z.array(z.string()).optional(),
  mizanCompliance: z.object({
    threeEnginePattern: z.boolean(),
    tenantIsolation: z.boolean(),
    agentTriggering: z.boolean(),
    moduleIntegration: z.boolean()
  })
});

const TestSuiteResultSchema = z.object({
  totalTests: z.number(),
  passedTests: z.number(),
  failedTests: z.number(),
  duration: z.number(),
  results: z.array(TestResultSchema),
  mizanPlatformCompliance: z.boolean(),
  overallScore: z.number()
});

type TestResult = z.infer<typeof TestResultSchema>;
type TestSuiteResult = z.infer<typeof TestSuiteResultSchema>;

interface MizanTestConfig {
  tenantId: string;
  testEnvironment: 'development' | 'staging' | 'production';
  enableMizanValidation: boolean;
  testModules: ('culture' | 'skills' | 'performance' | 'hiring' | 'lxp' | 'talent' | 'bonus')[];
}

interface TestContext {
  startTime: number;
  config: MizanTestConfig;
  results: TestResult[];
}

/**
 * Enhanced validation test runner with strict TypeScript compliance
 * Validates Mizan platform architecture and business logic integrity
 */
async function runTests(config: MizanTestConfig): Promise<TestSuiteResult> {
  const context: TestContext = {
    startTime: Date.now(),
    config,
    results: []
  };

  try {
    // Validate configuration
    const validatedConfig = await validateTestConfig(config);

    // Run Mizan-specific architecture tests
    await runMizanArchitectureTests(context);

    // Run business workflow tests
    await runBusinessWorkflowTests(context);

    // Run agent triggering tests
    await runAgentTriggeringTests(context);

    // Run multi-tenant isolation tests
    await runTenantIsolationTests(context);

    // Compile results
    return compileTestResults(context);

  } catch (error) {
    console.error('Test execution failed:', error);
    throw new Error(`Mizan test suite failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

/**
 * Validates test configuration for Mizan compliance
 */
async function validateTestConfig(config: MizanTestConfig): Promise<MizanTestConfig> {
  if (!config.tenantId || config.tenantId.trim().length === 0) {
    throw new Error('tenantId is required for Mizan multi-tenant testing');
  }

  if (!config.testModules || config.testModules.length === 0) {
    throw new Error('At least one test module must be specified');
  }

  return config;
}

