/**
 * Compiles test results into final suite result
 */
function compileTestResults(context: TestContext): TestSuiteResult {
  const totalTests = context.results.length;
  const passedTests = context.results.filter(r => r.passed).length;
  const failedTests = totalTests - passedTests;
  const duration = Date.now() - context.startTime;

  const mizanCompliance = context.results.every(r => 
    r.mizanCompliance.threeEnginePattern || 
    r.mizanCompliance.tenantIsolation || 
    r.mizanCompliance.agentTriggering || 
    r.mizanCompliance.moduleIntegration
  );

  const overallScore = totalTests > 0 ? (passedTests / totalTests) * 100 : 0;

  return TestSuiteResultSchema.parse({
    totalTests,
    passedTests,
    failedTests,
    duration,
    results: context.results,
    mizanPlatformCompliance: mizanCompliance && passedTests === totalTests,
    overallScore
  });
}

