/**
 * Mizan Strategic Report Generator
 * Implements Three-Engine Architecture for strategic analysis
 * Maintains tenant isolation and business workflow integrity
 */
export class MizanStrategicIntegrator {
  private knowledgeEngine: StrategicKnowledgeEngine;
  private dataEngine: StrategicDataEngine;
  private reasoningEngine: StrategicReasoningEngine;

  constructor() {
    this.knowledgeEngine = new StrategicKnowledgeEngine();
    this.dataEngine = new StrategicDataEngine();
    this.reasoningEngine = new StrategicReasoningEngine();
  }

  /**
   * Generate comprehensive Mizan strategic report
   * Validates input data and maintains tenant isolation
   */
  async generateMizanStrategicReport(allAgentData: MizanAgentData): Promise<MizanStrategicReport> {
    try {
      // Validate input data with strict TypeScript types
      const validatedData = MizanAgentDataSchema.parse(allAgentData);

      // Ensure tenant isolation
      if (!validatedData.tenantId) {
        throw new Error('Tenant ID is required for strategic report generation');
      }

      // Knowledge Engine: Get strategic frameworks and best practices
      const strategicContext = await this.knowledgeEngine.getStrategicContext({
        industry: validatedData.organizationId,
        organizationSize: this.calculateOrgSize(validatedData),
        maturityLevel: this.assessMaturityLevel(validatedData)
      });

      // Data Engine: Process and normalize all agent data
      const processedData = await this.dataEngine.processAgentData(validatedData, strategicContext);

      // Reasoning Engine: Generate strategic insights and recommendations
      const strategicAnalysis = await this.reasoningEngine.analyzeStrategicAlignment(
        processedData,
        strategicContext
      );

      // Generate comprehensive report
      const report: MizanStrategicReport = {
        tenantId: validatedData.tenantId,
        reportId: crypto.randomUUID(),
        generatedAt: new Date(),

        executiveSummary: {
          organizationalReadiness: strategicAnalysis.overallReadiness,
          strategicAlignment: strategicAnalysis.alignmentScore,
          criticalActions: strategicAnalysis.criticalActions,
          timeToStrategicReadiness: strategicAnalysis.timeToReadiness
        },

        moduleInsights: {
          structure: this.generateStructureInsights(validatedData.structure, strategicAnalysis),
          culture: this.generateCultureInsights(validatedData.culture, strategicAnalysis),
          skills: this.generateSkillsInsights(validatedData.skills, strategicAnalysis),
          performance: this.generatePerformanceInsights(validatedData.performance, strategicAnalysis)
        },

        strategicRecommendations: strategicAnalysis.recommendations,

        integrationHealth: {
          agentTriggeringSuccess: this.assessAgentTriggeringHealth(validatedData),
          moduleActivationSuccess: this.assessModuleActivationHealth(validatedData),
          dataFlowIntegrity: this.assessDataFlowIntegrity(validatedData),
          tenantIsolationCompliance: true // Validated by schema
        }
      };

      // Validate output
      return MizanStrategicReportSchema.parse(report);

    } catch (error) {
      console.error('Strategic report generation failed:', error);
      throw new Error(`Failed to generate strategic report: ${error.message}`);
    }
  }

  private calculateOrgSize(data: MizanAgentData): 'small' | 'medium' | 'large' | 'enterprise' {
    const totalHeadCount = data.structure.departmentalStructure.reduce(
      (sum, dept) => sum + dept.headCount, 0
    );

    if (totalHeadCount < 50) return 'small';
    if (totalHeadCount < 200) return 'medium';
    if (totalHeadCount < 1000) return 'large';
    return 'enterprise';
  }

  private assessMaturityLevel(data: MizanAgentData): 'startup' | 'growth' | 'mature' | 'enterprise' {
    const indicators = [
      data.structure.organizationalReadiness,
      data.culture.overallCultureScore,
      data.skills.strategicAlignment,
      data.performance.overallPerformanceScore
    ];

    const averageMaturity = indicators.reduce((sum, score) => sum + score, 0) / indicators.length;

    if (averageMaturity < 40) return 'startup';
    if (averageMaturity < 60) return 'growth';
    if (averageMaturity < 80) return 'mature';
    return 'enterprise';
  }

  private generateStructureInsights(structure: MizanAgentData['structure'], analysis: any) {
    return {
      strengths: [
        `Organizational readiness at ${structure.organizationalReadiness}%`,
        `${structure.departmentalStructure.length} departments with clear structure`
      ],
      gaps: structure.positionCriticality
        .filter(pos => pos.criticalityScore > 80 && pos.successionUrgency === 'immediate')
        .map(pos => `Critical succession gap: ${pos.positionTitle}`),
      recommendations: [
        'Implement succession planning for critical positions',
        'Strengthen departmental reporting structures'
      ]
    };
  }

  private generateCultureInsights(culture: MizanAgentData['culture'], analysis: any) {
    return {
      currentState: culture.overallCultureScore > 70 ? 'Strong' : 
                   culture.overallCultureScore > 50 ? 'Developing' : 'Needs Attention',
      agentActivations: {
        recognition: culture.recognitionAgent.triggered,
        engagement: culture.engagementAgent.triggered
      },
      transformationPlan: culture.leadershipPriorities.map(p => p.priority)
    };
  }

  private generateSkillsInsights(skills: MizanAgentData['skills'], analysis: any) {
    return {
      readinessLevel: skills.strategicReadiness,
      criticalGaps: skills.criticalGaps
        .filter(gap => gap.gapSeverity === 'critical')
        .map(gap => gap.skillCategory),
      lxpActivations: skills.lxpTriggers.filter(trigger => trigger.triggered).length,
      developmentTimeline: skills.organizationSkillsAssessment.timeToReadiness
    };
  }

  private generatePerformanceInsights(performance: MizanAgentData['performance'], analysis: any) {
    return {
      cycleHealth: performance.cycleStatus === 'completed' ? 'Healthy' : 'In Progress',
      goalAlignment: performance.goalCompletionRate,
      moduleTriggered: {
        talent: performance.talentTriggers.length > 0,
        bonus: performance.bonusTriggers.length > 0
      }
    };
  }

  private assessAgentTriggeringHealth(data: MizanAgentData): number {
    const triggers = [
      data.culture.recognitionAgent.triggered,
      data.culture.engagementAgent.triggered,
      data.skills.lxpTriggers.some(t => t.triggered)
    ];

    return (triggers.filter(Boolean).length / triggers.length) * 100;
  }

  private assessModuleActivationHealth(data: MizanAgentData): number {
    const modules = [
      data.triggeredModules.lxp.active,
      data.triggeredModules.talent.active,
      data.triggeredModules.bonus.active
    ];

    return (modules.filter(Boolean).length / modules.length) * 100;
  }

  private assessDataFlowIntegrity(data: MizanAgentData): number {
    // Check if all required tenant IDs are present and consistent
    const tenantConsistency = data.tenantId ? 100 : 0;

    // Check if agent triggering data is complete
    const agentDataCompleteness = (
      data.culture.recognitionAgent && 
      data.culture.engagementAgent && 
      data.skills.lxpTriggers
    ) ? 100 : 0;

    return (tenantConsistency + agentDataCompleteness) / 2;
  }
}

