/**
 * Mizan-Intelligent Security Validator
 * Validates security compliance across the entire Mizan platform
 * Ensures Three-Engine Architecture, agent triggering, and tenant isolation
 */
async function validateMizanSecurity(
  config: MizanSecurityConfig
): Promise<SecurityValidationResult> {
  try {
    // Validate input configuration
    const validatedConfig = SecurityConfigSchema.parse(config);

    const violations: SecurityViolation[] = [];
    let complianceScore = 100;

    // Initialize Mizan architecture compliance tracking
    const architectureCompliance = {
      threeEnginePattern: true,
      agentTriggering: true,
      tenantIsolation: true,
      moduleIntegration: true
    };

    // Validate each rule against Mizan-specific requirements
    for (const rule of validatedConfig.validationRules) {
      try {
        const context: ValidationContext = {
          filePath: validatedConfig.projectRoot,
          fileContent: '',
          tenantId: validatedConfig.tenantId,
          moduleType: 'core',
          mizanArchitecture: {
            isThreeEngine: true,
            hasAgentTriggering: true,
            requiresTenantIsolation: true
          }
        };

        // Validate context before processing
        ValidationContextSchema.parse(context);

        const ruleViolations = await rule.validator(context);
        violations.push(...ruleViolations);

        // Adjust compliance score based on violations
        ruleViolations.forEach(violation => {
          switch (violation.severity) {
            case 'critical':
              complianceScore -= 25;
              break;
            case 'high':
              complianceScore -= 15;
              break;
            case 'medium':
              complianceScore -= 10;
              break;
            case 'low':
              complianceScore -= 5;
              break;
          }
        });

      } catch (ruleError) {
        console.error(`Error validating rule ${rule.id}:`, ruleError);
        violations.push({
          ruleId: rule.id,
          severity: 'high',
          message: `Rule validation failed: ${ruleError instanceof Error ? ruleError.message : 'Unknown error'}`,
          filePath: validatedConfig.projectRoot,
          lineNumber: 0,
          suggestion: 'Review rule implementation and ensure proper error handling',
          mizanImpact: {
            affectedModules: ['Security'],
            workflowsAtRisk: ['Quality Assurance'],
            businessRisk: 'Security validation may be incomplete'
          }
        });
        complianceScore -= 10;
      }
    }

    // Ensure minimum compliance score
    complianceScore = Math.max(0, complianceScore);

    // Generate Mizan-specific recommendations
    const recommendations = generateMizanRecommendations(violations, architectureCompliance);

    // Identify critical issues that could break Mizan workflows
    const criticalIssues = violations.filter(v => 
      v.severity === 'critical' || 
      v.mizanImpact.workflowsAtRisk.length > 0
    );

    return {
      isSecure: violations.length === 0 && complianceScore >= 80,
      violations,
      complianceScore,
      mizanArchitectureCompliance: architectureCompliance,
      recommendations,
      criticalIssues
    };

  } catch (error) {
    console.error('Mizan security validation failed:', error);

    // Return safe fallback result
    return {
      isSecure: false,
      violations: [{
        ruleId: 'VALIDATION_ERROR',
        severity: 'critical',
        message: `Security validation failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
        filePath: config.projectRoot,
        lineNumber: 0,
        suggestion: 'Review security validation configuration and ensure all dependencies are available',
        mizanImpact: {
          affectedModules: ['All'],
          workflowsAtRisk: ['Security Validation'],
          businessRisk: 'Unable to validate platform security compliance'
        }
      }],
      complianceScore: 0,
      mizanArchitectureCompliance: {
        threeEnginePattern: false,
        agentTriggering: false,
        tenantIsolation: false,
        moduleIntegration: false
      },
      recommendations: [
        'Fix security validation configuration',
        'Ensure all required dependencies are installed',
        'Review error logs for specific issues'
      ],
      criticalIssues: []
    };
  }
}

/**
 * Generate Mizan-specific security recommendations
 */
function generateMizanRecommendations(
  violations: SecurityViolation[],
  architectureCompliance: SecurityValidationResult['mizanArchitectureCompliance']
): string[] {
  const recommendations: string[] = [];

  // Three-Engine Architecture recommendations
  if (!architectureCompliance.threeEnginePattern) {
    recommendations.push('Implement Three-Engine Architecture (Knowledge  Data  Reasoning) for AI features');
  }

  // Agent triggering recommendations
  if (!architectureCompliance.agentTriggering) {
    recommendations.push('Ensure proper agent triggering mechanisms (Culture  Recognition/Engagement)');
  }

  // Tenant isolation recommendations
  if (!architectureCompliance.tenantIsolation) {
    recommendations.push('Implement multi-tenant isolation with tenantId in all database queries');
  }

  // Module integration recommendations
  if (!architectureCompliance.moduleIntegration) {
    recommendations.push('Maintain proper module integration workflows (Skills  LXP activation)');
  }

  // Violation-specific recommendations
  const criticalViolations = violations.filter(v => v.severity === 'critical');
  if (criticalViolations.length > 0) {
    recommendations.push('Address critical security violations immediately to prevent business workflow disruption');
  }

  const tenantViolations = violations.filter(v => 
    v.message.toLowerCase().includes('tenant') || 
    v.mizanImpact.workflowsAtRisk.includes('Multi-tenant Isolation')
  );
  if (tenantViolations.length > 0) {
    recommendations.push('Review and fix tenant isolation issues to prevent data leakage between clients');
  }

  return recommendations;
}

/**
 * Validate specific Mizan module security
 */
async function validateMizanModuleSecurity(
  moduleName: string,
  filePath: string,
  tenantId: string
): Promise<SecurityViolation[]> {
  const violations: SecurityViolation[] = [];

  try {
    if (!existsSync(filePath)) {
      violations.push({
        ruleId: 'MODULE_NOT_FOUND',
        severity: 'high',
        message: `Mizan module file not found: ${filePath}`,
        filePath,
        lineNumber: 0,
        suggestion: 'Ensure module file exists and is properly configured',
        mizanImpact: {
          affectedModules: [moduleName],
          workflowsAtRisk: [`${moduleName} Module Workflows`],
          businessRisk: `${moduleName} functionality may be unavailable`
        }
      });
      return violations;
    }

    const fileContent = readFileSync(filePath, 'utf-8');

    // Check for tenant isolation
    if (!fileContent.includes('tenantId') && moduleName !== 'Authentication') {
      violations.push({
        ruleId: 'MISSING_TENANT_ISOLATION',
        severity: 'critical',
        message: `Missing tenant isolation in ${moduleName} module`,
        filePath,
        lineNumber: 0,
        suggestion: 'Add tenantId parameter to all database queries and API endpoints',
        mizanImpact: {
          affectedModules: [moduleName],
          workflowsAtRisk: ['Multi-tenant Data Isolation'],
          businessRisk: 'Potential data leakage between different client organizations'
        }
      });
    }

    // Check for Three-Engine Architecture in AI modules
    const aiModules = ['Culture', 'Skills', 'Structure', 'Performance', 'Hiring'];
    if (aiModules.includes(moduleName)) {
      const hasKnowledgeEngine = fileContent.includes('KnowledgeEngine');
      const hasDataEngine = fileContent.includes('DataEngine');
      const hasReasoningEngine = fileContent.includes('ReasoningEngine');

      if (!hasKnowledgeEngine || !hasDataEngine || !hasReasoningEngine) {
        violations.push({
          ruleId: 'INCOMPLETE_THREE_ENGINE',
          severity: 'high',
          message: `Incomplete Three-Engine Architecture in ${moduleName} module`,
          filePath,
          lineNumber: 0,
          suggestion: 'Implement all three engines: Knowledge, Data, and Reasoning',
          mizanImpact: {
            affectedModules: [moduleName],
            workflowsAtRisk: [`${moduleName} Analysis Workflow`],
            businessRisk: 'AI analysis may be incomplete or unreliable'
          }
        });
      }
    }

  } catch (error) {
    violations.push({
      ruleId: 'MODULE_VALIDATION_ERROR',
      severity: 'medium',
      message: `Error validating ${moduleName} module: ${error instanceof Error ? error.message : 'Unknown error'}`,
      filePath,
      lineNumber: 0,
      suggestion: 'Review module file for syntax errors or access permissions',
      mizanImpact: {
        affectedModules: [moduleName],
        workflowsAtRisk: ['Security Validation'],
        businessRisk: 'Unable to validate module security compliance'
      }
    });
  }

  return violations;
}

