/**
 * Validates critical Mizan platform dependencies
 * Ensures correct versions of Drizzle ORM, Next.js 14, TypeScript 5.x
 */
async function validateMizanDependencies(): Promise<MizanDependencyCheck[]> {
  const requiredDependencies = [
    { name: 'next', version: '^14.0.0', category: 'core' as const, criticality: 'critical' as const },
    { name: 'typescript', version: '^5.0.0', category: 'core' as const, criticality: 'critical' as const },
    { name: 'drizzle-orm', version: '*', category: 'database' as const, criticality: 'critical' as const },
    { name: 'react', version: '^18.0.0', category: 'core' as const, criticality: 'critical' as const },
    { name: 'zod', version: '*', category: 'security' as const, criticality: 'high' as const },
    { name: 'jsonwebtoken', version: '*', category: 'security' as const, criticality: 'high' as const },
    { name: 'bcryptjs', version: '*', category: 'security' as const, criticality: 'high' as const }
  ];

  const checks: MizanDependencyCheck[] = [];

  for (const dep of requiredDependencies) {
    try {
      const packageJson = require('../../package.json');
      const currentVersion = packageJson.dependencies?.[dep.name] || packageJson.devDependencies?.[dep.name] || 'not-found';

      checks.push({
        packageName: dep.name,
        requiredVersion: dep.version,
        currentVersion,
        isCompliant: currentVersion !== 'not-found' && (dep.version === '*' || currentVersion.includes(dep.version.replace('^', ''))),
        category: dep.category,
        criticality: dep.criticality
      });
    } catch (error) {
      checks.push({
        packageName: dep.name,
        requiredVersion: dep.version,
        currentVersion: 'error',
        isCompliant: false,
        category: dep.category,
        criticality: dep.criticality
      });
    }
  }

  return checks;
}

