/**
 * Calculates overall compliance score for Mizan platform
 */
function calculateComplianceScore(
  dependencies: MizanDependencyCheck[],
  architecture: MizanArchitectureValidation,
  violations: SecurityViolation[]
): number {
  const depScore = dependencies.filter(d => d.isCompliant).length / dependencies.length * 40;
  const archScore = Object.values(architecture).filter(Boolean).length / Object.keys(architecture).length * 40;
  const violationPenalty = violations.reduce((penalty, v) => {
    const severityPenalty = { critical: 10, high: 5, medium: 2, low: 1 };
    return penalty + severityPenalty[v.severity];
  }, 0);

  return Math.max(0, Math.min(100, depScore + archScore - violationPenalty));
}

/**
 * Generates Mizan-specific recommendations for compliance improvements
 */
function generateMizanRecommendations(
  dependencies: MizanDependencyCheck[],
  architecture: MizanArchitectureValidation,
  violations: SecurityViolation[]
): string[] {
  const recommendations: string[] = [];

  // Dependency recommendations
  const failedDeps = dependencies.filter(d => !d.isCompliant);
  if (failedDeps.length > 0) {
    recommendations.push(`Update ${failedDeps.length} non-compliant dependencies: ${failedDeps.map(d => d.packageName).join(', ')}`);
  }

  // Architecture recommendations
  if (!architecture.threeEnginePattern) {
    recommendations.push('Implement Three-Engine Architecture (Knowledge  Data  Reasoning) for AI modules');
  }
  if (!architecture.agentTriggering) {
    recommendations.push('Fix agent triggering mechanisms (Culture  Recognition/Engagement)');
  }
  if (!architecture.moduleIntegration) {
    recommendations.push('Restore module integration workflows (Skills  LXP, Performance  Talent/Bonus)');
  }
  if (!architecture.tenantIsolation) {
    recommendations.push('Implement tenant isolation with tenantId in all database queries');
  }

  // Security recommendations
  const criticalViolations = violations.filter(v => v.severity === 'critical');
  if (criticalViolations.length > 0) {
    recommendations.push(`Address ${criticalViolations.length} critical security violations immediately`);
  }

  return recommendations;
}

