export interface RejectionCriteria {
  type: CriteriaType;
  rules: RejectionRule[];
}

export interface RejectionRule {
  id: string;
  name: string;
  description: string;
  severity: 'low' | 'medium' | 'high' | 'critical';
  checkFunction: (fixData: FixData) => boolean;
  rejectionMessage: string;
  mizanSpecific: boolean;
}

export interface RejectionResult {
  shouldReject: boolean;
  rejectedRules: RejectionRule[];
  rejectionReasons: string[];
  confidence: number;
  mizanCompliance: boolean;
}

/**
 * Mizan Platform - Check Rejection Criteria
 * Validates fix data against Mizan-specific business and technical rules
 * Ensures production-ready code quality and platform compliance
 */
export function checkRejectionCriteria(
  fixData: FixData, 
  criteriaType: CriteriaType = 'BUSINESS_REJECTS'
): RejectionResult {
  try {
    // Validate input parameters
    if (!fixData || typeof fixData !== 'object') {
      throw new Error('Invalid fixData: must be a valid FixData object');
    }

    if (!fixData.id || !fixData.file || !fixData.rule) {
      throw new Error('Invalid fixData: missing required fields (id, file, rule)');
    }

    // Get rejection criteria based on type
    const criteria = getRejectionCriteria(criteriaType);
    const rejectedRules: RejectionRule[] = [];
    const rejectionReasons: string[] = [];

    // Apply each rule to the fix data
    for (const rule of criteria.rules) {
      try {
        if (rule.checkFunction(fixData)) {
          rejectedRules.push(rule);
          rejectionReasons.push(rule.rejectionMessage);
        }
      } catch (error) {
        console.error(`Error applying rejection rule ${rule.id}:`, error);
        // Continue with other rules even if one fails
      }
    }

    // Calculate confidence based on rule matches and severity
    const confidence = calculateConfidence(rejectedRules, fixData);

    // Check Mizan-specific compliance
    const mizanCompliance = checkMizanCompliance(fixData, rejectedRules);

    return {
      shouldReject: rejectedRules.length > 0,
      rejectedRules,
      rejectionReasons,
      confidence,
      mizanCompliance
    };

  } catch (error) {
    console.error('Error in checkRejectionCriteria:', error);

    // Return safe default for error cases
    return {
      shouldReject: true,
      rejectedRules: [],
      rejectionReasons: [`System error: ${error instanceof Error ? error.message : 'Unknown error'}`],
      confidence: 0,
      mizanCompliance: false
    };
  }
}

/**
 * Get rejection criteria configuration based on type
 */
function getRejectionCriteria(criteriaType: CriteriaType): RejectionCriteria {
  const criteriaMap: Record<CriteriaType, RejectionCriteria> = {
    'BUSINESS_REJECTS': getBusinessRejectionCriteria(),
    'SECURITY_REJECTS': getSecurityRejectionCriteria(),
    'ARCHITECTURE_REJECTS': getArchitectureRejectionCriteria(),
    'MIZAN_COMPLIANCE_REJECTS': getMizanComplianceRejectionCriteria()
  };

  return criteriaMap[criteriaType] || criteriaMap['BUSINESS_REJECTS'];
}

/**
 * Business-specific rejection criteria for Mizan Platform
 */
function getBusinessRejectionCriteria(): RejectionCriteria {
  return {
    type: 'BUSINESS_REJECTS',
    rules: [
      {
        id: 'MOCK_DATA_VIOLATION',
        name: 'Mock Data Usage',
        description: 'Code contains mock data or placeholders',
        severity: 'critical',
        checkFunction: (fixData: FixData) => {
          const mockPatterns = [
            /mock\s*data/i,
            /placeholder/i,
            /TODO.*implement/i,
            /sample\s*user/i,
            /test\s*data/i,
            /dummy\s*data/i
          ];
          return mockPatterns.some(pattern => pattern.test(fixData.code));
        },
        rejectionMessage: 'Fix contains mock data or placeholders. Mizan requires production-ready implementations only.',
        mizanSpecific: true
      },
      {
        id: 'TENANT_ISOLATION_MISSING',
        name: 'Missing Tenant Isolation',
        description: 'Database query missing tenantId for multi-tenant isolation',
        severity: 'critical',
        checkFunction: (fixData: FixData) => {
          const hasDbQuery = /db\.(select|insert|update|delete)/i.test(fixData.code);
          const hasTenantId = /tenantId/i.test(fixData.code);
          return hasDbQuery && !hasTenantId && fixData.mizanModule !== undefined;
        },
        rejectionMessage: 'Database operations must include tenantId for multi-tenant isolation.',
        mizanSpecific: true
      },
      {
        id: 'THREE_ENGINE_VIOLATION',
        name: 'Three-Engine Architecture Violation',
        description: 'AI analysis not following Three-Engine pattern',
        severity: 'high',
        checkFunction: (fixData: FixData) => {
          const isAIModule = ['culture', 'structure', 'skills', 'performance', 'hiring'].includes(fixData.mizanModule || '');
          const hasEnginePattern = /KnowledgeEngine|DataEngine|ReasoningEngine/i.test(fixData.code);
          return isAIModule && !hasEnginePattern && /analyze|process|generate/i.test(fixData.code);
        },
        rejectionMessage: 'AI analysis modules must follow Three-Engine Architecture pattern.',
        mizanSpecific: true
      }
    ]
  };
}

/**
