    rules: [
      {
        id: 'WORKFLOW_BREAKING',
        name: 'Workflow Breaking Change',
        description: 'Change breaks Mizan business workflows',
        severity: 'critical',
        checkFunction: (fixData: FixData) => {
          const criticalWorkflows = [
            'CultureRecognition',
            'CultureEngagement', 
            'SkillsLXP',
            'PerformanceTalent',
            'PerformanceBonus'
          ];
          return fixData.affectedWorkflows?.some(workflow => 
            criticalWorkflows.includes(workflow)
          ) || false;
        },
        rejectionMessage: 'Fix breaks critical Mizan business workflows.',
        mizanSpecific: true
      }
    ]
  };
}

/**
 * Calculate confidence score based on rejected rules and fix data
 */
function calculateConfidence(rejectedRules: RejectionRule[], fixData: FixData): number {
  if (rejectedRules.length === 0) {
    return 1.0; // High confidence if no rules violated
  }

  // Weight by severity
  const severityWeights = { low: 0.1, medium: 0.3, high: 0.6, critical: 1.0 };
  const totalWeight = rejectedRules.reduce((sum, rule) => sum + severityWeights[rule.severity], 0);
  const maxPossibleWeight = rejectedRules.length * 1.0; // All critical

  // Inverse confidence - more violations = lower confidence
  return Math.max(0, 1 - (totalWeight / maxPossibleWeight));
}

/**
 * Check Mizan-specific compliance
 */
function checkMizanCompliance(fixData: FixData, rejectedRules: RejectionRule[]): boolean {
  // Check if any Mizan-specific rules were violated
  const mizanViolations = rejectedRules.filter(rule => rule.mizanSpecific);
  return mizanViolations.length === 0;
}

