/**
 * Checks all rejection criteria against fix data
 * Ensures Mizan platform integrity and business workflow compliance
 */
function checkAllRejectionCriteria(fixData: FixData): RejectionResult {
  try {
    const rejectedCriteria: RejectionCriterion[] = [];
    let highestSeverity: 'low' | 'medium' | 'high' | 'critical' = 'low';

    // Validate input
    if (!fixData || !fixData.file || !fixData.code) {
      throw new Error('Invalid fix data provided');
    }

    // Check each criterion
    for (const criterion of MIZAN_REJECTION_CRITERIA) {
      try {
        if (criterion.validator(fixData)) {
          rejectedCriteria.push(criterion);

          // Update highest severity
          const severityLevels = { low: 1, medium: 2, high: 3, critical: 4 };
          if (severityLevels[criterion.severity] > severityLevels[highestSeverity]) {
            highestSeverity = criterion.severity;
          }
        }
      } catch (error) {
        console.error(`Error validating criterion ${criterion.id}:`, error);
        // Continue with other criteria
      }
    }

    // Calculate confidence based on number of criteria checked
    const confidence = Math.max(0.7, 1 - (rejectedCriteria.length * 0.1));

    // Determine Mizan compliance
    const mizanSpecificViolations = rejectedCriteria.filter(c => c.mizanSpecific);
    const mizanCompliance = mizanSpecificViolations.length === 0;

    // Generate recommendations
    const recommendations: string[] = [];
    if (rejectedCriteria.length > 0) {
      recommendations.push('Fix must address all rejected criteria before proceeding');

      if (mizanSpecificViolations.length > 0) {
        recommendations.push('Critical: Mizan-specific architecture violations must be resolved');
      }

      const criticalViolations = rejectedCriteria.filter(c => c.severity === 'critical');
      if (criticalViolations.length > 0) {
        recommendations.push('Critical violations detected - immediate attention required');
      }
    }

    return {
      shouldReject: rejectedCriteria.length > 0,
      rejectedCriteria,
      confidence,
      mizanCompliance,
      businessRisk: highestSeverity,
      recommendations
    };

  } catch (error) {
    console.error('Error in checkAllRejectionCriteria:', error);

    // Return safe default for error cases
    return {
      shouldReject: true,
      rejectedCriteria: [],
      confidence: 0.0,
      mizanCompliance: false,
      businessRisk: 'critical',
      recommendations: ['Error occurred during validation - manual review required']
    };
  }
}

/**
 * Validates Mizan-specific business workflow integrity
 */
function validateMizanWorkflows(fixData: FixData): boolean {
  const mizanCriteria = MIZAN_REJECTION_CRITERIA.filter(c => c.mizanSpecific);

  for (const criterion of mizanCriteria) {
    if (criterion.validator(fixData)) {
      return false; // Workflow integrity compromised
    }
  }

  return true; // All Mizan workflows preserved
}

