/**
 * Parses Mizan Fix XML content with strict TypeScript compliance
 * Ensures production-ready parsing for multi-agent system operations
 * @param {string} xmlContent - The XML content to parse
 * @returns {MizanFixXML} Parsed fix object with complete type safety
 */
function parseMizanFixXML(xmlContent: string): MizanFixXML {
  if (!xmlContent || typeof xmlContent !== 'string') {
    throw new Error('Invalid XML content: must be a non-empty string');
  }

  try {
    // Production-ready XML parsing with error handling
    const parser = new DOMParser();
    const xmlDoc = parser.parseFromString(xmlContent, 'text/xml');

    // Check for parsing errors
    const parserError = xmlDoc.querySelector('parsererror');
    if (parserError) {
      throw new Error(`XML parsing failed: ${parserError.textContent}`);
    }

    const mizanFixElement = xmlDoc.querySelector('mizanFix');
    if (!mizanFixElement) {
      throw new Error('Invalid Mizan Fix XML: missing mizanFix root element');
    }

    // Extract with type safety
    const confidence = parseFloat(mizanFixElement.querySelector('confidence')?.textContent || '0');
    const complexity = mizanFixElement.querySelector('complexity')?.textContent as MizanComplexity || 'simple';
    const mizanCompliance = mizanFixElement.querySelector('mizanCompliance')?.textContent === 'true';
    const businessImpact = mizanFixElement.querySelector('businessImpact')?.textContent as BusinessImpact || 'neutral';

    // Parse primary fix with validation
    const primaryFixElement = mizanFixElement.querySelector('primaryFix');
    if (!primaryFixElement) {
      throw new Error('Invalid Mizan Fix XML: missing primaryFix element');
    }

    const primaryFix: MizanPrimaryFix = {
      file: primaryFixElement.querySelector('file')?.textContent || '',
      startLine: parseInt(primaryFixElement.querySelector('startLine')?.textContent || '0'),
      endLine: parseInt(primaryFixElement.querySelector('endLine')?.textContent || '0'),
      oldCode: primaryFixElement.querySelector('oldCode')?.textContent || '',
      newCode: primaryFixElement.querySelector('newCode')?.textContent || '',
      explanation: primaryFixElement.querySelector('explanation')?.textContent || ''
    };

    // Parse Mizan architecture compliance
    const archElement = mizanFixElement.querySelector('mizanArchitecture');
    const mizanArchitecture: MizanArchitecture = {
      threeEngineCompliance: archElement?.querySelector('threeEngineCompliance')?.textContent === 'true',
      agentTriggering: archElement?.querySelector('agentTriggering')?.textContent === 'true',
      moduleIntegration: archElement?.querySelector('moduleIntegration')?.textContent === 'true',
      tenantIsolation: archElement?.querySelector('tenantIsolation')?.textContent === 'true',
      workflowCompleteness: archElement?.querySelector('workflowCompleteness')?.textContent === 'true'
    };

    // Parse business logic impact
    const businessElement = mizanFixElement.querySelector('businessLogic');
    const businessLogic: MizanBusinessLogic = {
      affectedModules: Array.from(businessElement?.querySelectorAll('affectedModules') || [])
        .map(el => el.textContent || ''),
      workflowsRestored: Array.from(businessElement?.querySelectorAll('workflowsRestored') || [])
        .map(el => el.textContent || ''),
      strategicAlignment: businessElement?.querySelector('strategicAlignment')?.textContent as StrategicAlignment || 'maintained'
    };

    // Parse additional files if present
    const additionalFiles: MizanAdditionalFile[] = Array.from(mizanFixElement.querySelectorAll('additionalFiles file'))
      .map(fileEl => ({
        path: fileEl.querySelector('path')?.textContent || '',
        content: fileEl.querySelector('content')?.textContent || '',
        reason: fileEl.querySelector('reason')?.textContent || ''
      }));

    // Parse dependencies
    const dependencies: string[] = Array.from(mizanFixElement.querySelectorAll('dependencies dependency'))
      .map(dep => dep.textContent || '');

    // Parse migration steps
    const migrationSteps: string[] = Array.from(mizanFixElement.querySelectorAll('migrationSteps step'))
      .map(step => step.textContent || '');

    // Parse testing strategy
    const testingElement = mizanFixElement.querySelector('testingStrategy');
    const testingStrategy: MizanTestingStrategy = {
      unitTests: testingElement?.querySelector('unitTests')?.textContent || '',
      integrationTests: testingElement?.querySelector('integrationTests')?.textContent || '',
      businessTests: testingElement?.querySelector('businessTests')?.textContent || '',
      tenantTests: testingElement?.querySelector('tenantTests')?.textContent || ''
    };

    // Parse validation results
    const validationElement = mizanFixElement.querySelector('mizanValidation');
    const mizanValidation: MizanValidation = {
      platformCompliance: validationElement?.querySelector('platformCompliance')?.textContent === 'true',
      architectureIntegrity: validationElement?.querySelector('architectureIntegrity')?.textContent === 'true',
      businessWorkflows: validationElement?.querySelector('businessWorkflows')?.textContent as WorkflowStatus || 'maintained',
      productionReadiness: validationElement?.querySelector('productionReadiness')?.textContent === 'true',
      confidence: parseInt(validationElement?.querySelector('confidence')?.textContent || '0'),
      improvements: validationElement?.querySelector('improvements')?.textContent || ''
    };

    return {
      confidence,
      complexity,
      mizanCompliance,
      businessImpact,
      primaryFix,
      mizanArchitecture,
      businessLogic,
      additionalFiles,
      dependencies,
      migrationSteps,
      testingStrategy,
      mizanValidation
    };

  } catch (error) {
    throw new Error(`Failed to parse Mizan Fix XML: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

