        const startIndex = xml.indexOf(startTag);
        const endIndex = xml.indexOf(endTag);

        if (startIndex === -1 || endIndex === -1 || endIndex <= startIndex) {
            console.warn(`XML tag '${tag}' not found or malformed in content`);
            return null;
        }

        const content = xml.substring(startIndex + startTag.length, endIndex).trim();
        return content || null;
    } catch (error) {
        console.error('Error extracting XML content:', error);
