/**
 * Mizan Multi-Agent Consensus Tie-Breaker
 * Resolves conflicts between agents using weighted scoring and business logic priority
 * Maintains Three-Engine Architecture compliance and tenant isolation
 */
async function tieBreaker(
  conflictingAnalyses: AgentAnalysis[],
  tenantId: string,
  context: MizanContext
): Promise<ConsensusResult> {
  try {
    // Validate inputs with strict typing
    if (!conflictingAnalyses || conflictingAnalyses.length === 0) {
      throw new Error('No analyses provided for tie-breaking');
    }

    if (!tenantId || typeof tenantId !== 'string') {
      throw new Error('Valid tenantId required for multi-tenant isolation');
    }

    // Apply Mizan business logic priority weighting
    const weightedScores = conflictingAnalyses.map(analysis => ({
      ...analysis,
      businessWeight: calculateMizanBusinessWeight(analysis, context),
      architecturalCompliance: validateMizanArchitecture(analysis),
      tenantIsolation: analysis.tenantId === tenantId
    }));

    // Filter for tenant isolation compliance
    const tenantCompliantAnalyses = weightedScores.filter(
      analysis => analysis.tenantIsolation
    );

    if (tenantCompliantAnalyses.length === 0) {
      throw new Error(`No tenant-compliant analyses found for tenant: ${tenantId}`);
    }

    // Select winner based on Mizan-specific criteria
    const winner = tenantCompliantAnalyses.reduce((best, current) => {
      const bestScore = calculateMizanScore(best, context);
      const currentScore = calculateMizanScore(current, context);
      return currentScore > bestScore ? current : best;
    });

    return {
      selectedAnalysis: winner,
      confidence: winner.businessWeight * winner.architecturalCompliance,
      reasoning: generateMizanReasoning(winner, tenantCompliantAnalyses),
      tenantId,
      timestamp: new Date().toISOString(),
      mizanCompliant: true
    };

  } catch (error) {
    console.error(`Mizan tie-breaker error for tenant ${tenantId}:`, error);
    throw new MizanTieBreakerError(
      `Failed to resolve agent consensus: ${error.message}`,
      { tenantId, context, error }
    );
  }
}

