/**
 * Mizan Platform - ReasoningEngine Confidence Calculator
 * Core component of Three-Engine Architecture
 * Calculates confidence scores for all AI-powered analyses
 * 
 * Used by:
 * - Culture Analysis (including Recognition/Engagement agents)
 * - Skills Analysis (including Strategic Assessment)
 * - Structure Analysis
 * - Performance Module
 * - Hiring Module
 * - LXP Module
 * - Talent Module
 */
export function calculateConfidence(
  processedData: ProcessedData,
  context: AnalysisContext,
  additionalFactors?: Partial<ConfidenceFactors>
): ConfidenceResult {
  try {
    // Validate inputs with Zod schemas
    const validatedData = ProcessedDataSchema.parse(processedData);
    const validatedContext = AnalysisContextSchema.parse(context);

    // Calculate base confidence factors
    const factors: ConfidenceFactors = {
      // Data Quality Assessment (25% weight)
      dataQuality: calculateDataQuality(validatedData.quality),

      // Context Relevance (20% weight)
      contextRelevance: calculateContextRelevance(validatedContext, validatedData),

      // Framework Alignment (20% weight)
      frameworkAlignment: calculateFrameworkAlignment(validatedContext, validatedData),

      // Benchmark Coverage (15% weight)
      benchmarkCoverage: calculateBenchmarkCoverage(validatedContext.benchmarks, validatedData),

      // Industry Standards Match (10% weight)
      industryStandardsMatch: calculateIndustryMatch(validatedContext.industryStandards, validatedData),

      // Sample Size Factor (5% weight)
      sampleSize: calculateSampleSizeScore(validatedData),

      // Analysis Depth (5% weight)
      analysisDepth: calculateAnalysisDepth(validatedData, validatedContext),

      // Apply additional factors if provided
      ...additionalFactors
    };

    // Validate calculated factors
    const validatedFactors = ConfidenceFactorsSchema.parse(factors);

    // Calculate weighted overall confidence
    const overall = calculateWeightedConfidence(validatedFactors);

    // Determine reliability level
    const reliability = determineReliabilityLevel(overall);

    // Generate recommendations and risk factors
    const recommendations = generateRecommendations(validatedFactors, validatedContext);
    const riskFactors = identifyRiskFactors(validatedFactors, validatedContext);

    const result: ConfidenceResult = {
      overall,
      factors: validatedFactors,
      reliability,
      recommendations,
      riskFactors,
      tenantId: validatedContext.tenantId
    };

    // Log confidence calculation for audit trail
    console.log(`[Mizan ReasoningEngine] Confidence calculated for ${validatedContext.domain} analysis`, {
      tenantId: validatedContext.tenantId,
      overall: result.overall,
      reliability: result.reliability,
      timestamp: new Date().toISOString()
    });

    return result;

  } catch (error) {
    console.error('[Mizan ReasoningEngine] Confidence calculation error:', error);

    // Return safe fallback with low confidence
    return {
      overall: 0.3,
      factors: {
        dataQuality: 0.3,
        contextRelevance: 0.3,
        frameworkAlignment: 0.3,
        benchmarkCoverage: 0.3,
        industryStandardsMatch: 0.3,
        sampleSize: 0,
        analysisDepth: 0.3
      },
      reliability: 'low',
      recommendations: ['Data quality issues detected', 'Manual review required'],
      riskFactors: ['Calculation error occurred', 'Low confidence due to processing issues'],
      tenantId: context.tenantId || 'unknown'
    };
  }
}

// Supporting calculation functions with strict typing
function calculateDataQuality(quality: DataQuality): number {
  const weights = {
    completeness: 0.3,
    accuracy: 0.3,
    consistency: 0.2,
    reliability: 0.2
  };

  return (
    quality.completeness * weights.completeness +
    quality.accuracy * weights.accuracy +
    quality.consistency * weights.consistency +
    quality.reliability * weights.reliability
  );
}

function calculateContextRelevance(context: AnalysisContext, data: ProcessedData): number {
  // Domain-specific relevance calculation
  const domainFactors: Record<string, number> = {
    culture: calculateCultureRelevance(context, data),
    skills: calculateSkillsRelevance(context, data),
    structure: calculateStructureRelevance(context, data),
    performance: calculatePerformanceRelevance(context, data),
    hiring: calculateHiringRelevance(context, data)
  };

  return domainFactors[context.domain] || 0.5;
}

function calculateFrameworkAlignment(context: AnalysisContext, data: ProcessedData): number {
  if (context.frameworks.length === 0) return 0.5;

  const alignmentScores = context.frameworks.map(framework => {
    // Check if data structure aligns with framework requirements
    return assessFrameworkAlignment(framework, data, context.domain);
  });

  return alignmentScores.reduce((sum, score) => sum + score, 0) / alignmentScores.length;
}

function calculateBenchmarkCoverage(benchmarks: Record<string, number>, data: ProcessedData): number {
  const benchmarkKeys = Object.keys(benchmarks);
  if (benchmarkKeys.length === 0) return 0.5;

  const coveredBenchmarks = benchmarkKeys.filter(key => 
    data.structured[key] !== undefined || data.normalized[key] !== undefined
  );

  return coveredBenchmarks.length / benchmarkKeys.length;
}

function calculateIndustryMatch(industryStandards: Record<string, any>, data: ProcessedData): number {
  const standardKeys = Object.keys(industryStandards);
  if (standardKeys.length === 0) return 0.5;

  let matchScore = 0;
  let totalComparisons = 0;

  standardKeys.forEach(key => {
    if (data.structured[key] !== undefined) {
      const similarity = calculateSimilarity(data.structured[key], industryStandards[key]);
      matchScore += similarity;
      totalComparisons++;
    }
  });

  return totalComparisons > 0 ? matchScore / totalComparisons : 0.5;
}

function calculateSampleSizeScore(data: ProcessedData): number {
  // Extract sample size from structured data
  const sampleSize = extractSampleSize(data.structured);

  // Convert sample size to confidence factor (logarithmic scale)
  if (sampleSize < 10) return 0.3;
  if (sampleSize < 50) return 0.6;
  if (sampleSize < 100) return 0.8;
  return 1.0;
}

function calculateAnalysisDepth(data: ProcessedData, context: AnalysisContext): number {
  // Assess depth based on data structure complexity and context requirements
  const structureDepth = Object.keys(data.structured).length;
  const expectedDepth = getExpectedDepthForDomain(context.domain);

  return Math.min(structureDepth / expectedDepth, 1.0);
}

function calculateWeightedConfidence(factors: ConfidenceFactors): number {
  const weights = {
    dataQuality: 0.25,
    contextRelevance: 0.20,
    frameworkAlignment: 0.20,
    benchmarkCoverage: 0.15,
    industryStandardsMatch: 0.10,
    sampleSize: 0.05,
    analysisDepth: 0.05
  };

  const sampleSizeFactor = Math.min(factors.sampleSize / 100, 1.0);

  return (
    factors.dataQuality * weights.dataQuality +
    factors.contextRelevance * weights.contextRelevance +
    factors.frameworkAlignment * weights.frameworkAlignment +
    factors.benchmarkCoverage * weights.benchmarkCoverage +
    factors.industryStandardsMatch * weights.industryStandardsMatch +
    sampleSizeFactor * weights.sampleSize +
    factors.analysisDepth * weights.analysisDepth
  );
}

function determineReliabilityLevel(confidence: number): 'low' | 'medium' | 'high' | 'very_high' {
  if (confidence >= 0.85) return 'very_high';
  if (confidence >= 0.70) return 'high';
  if (confidence >= 0.50) return 'medium';
  return 'low';
}

function generateRecommendations(factors: ConfidenceFactors, context: AnalysisContext): string[] {
  const recommendations: string[] = [];

  if (factors.dataQuality < 0.7) {
    recommendations.push('Improve data quality through additional validation and cleaning');
  }

  if (factors.sampleSize < 50) {
    recommendations.push('Increase sample size for more reliable analysis');
  }

  if (factors.frameworkAlignment < 0.6) {
    recommendations.push(`Review ${context.domain} framework alignment and methodology`);
  }

  if (factors.benchmarkCoverage < 0.5) {
    recommendations.push('Expand benchmark coverage for better industry comparison');
  }

  return recommendations;
}

function identifyRiskFactors(factors: ConfidenceFactors, context: AnalysisContext): string[] {
  const risks: string[] = [];

  if (factors.dataQuality < 0.5) {
    risks.push('Low data quality may lead to unreliable insights');
  }

  if (factors.contextRelevance < 0.6) {
    risks.push('Analysis may not be fully relevant to organizational context');
  }

  if (factors.sampleSize < 20) {
    risks.push('Small sample size increases uncertainty in results');
  }

  return risks;
}

// Domain-specific relevance calculators
function calculateCultureRelevance(context: AnalysisContext, data: ProcessedData): number {
  // Culture-specific relevance factors
  const cultureIndicators = ['values', 'behaviors', 'communication', 'recognition', 'engagement'];
  const presentIndicators = cultureIndicators.filter(indicator => 
    data.structured[indicator] !== undefined
  );
  return presentIndicators.length / cultureIndicators.length;
}

function calculateSkillsRelevance(context: AnalysisContext, data: ProcessedData): number {
  // Skills-specific relevance factors
  const skillsIndicators = ['technical_skills', 'soft_skills', 'competencies', 'gaps', 'development'];
  const presentIndicators = skillsIndicators.filter(indicator => 
    data.structured[indicator] !== undefined
  );
  return presentIndicators.length / skillsIndicators.length;
}

function calculateStructureRelevance(context: AnalysisContext, data: ProcessedData): number {
  // Structure-specific relevance factors
  const structureIndicators = ['hierarchy', 'reporting', 'roles', 'departments', 'positions'];
  const presentIndicators = structureIndicators.filter(indicator => 
    data.structured[indicator] !== undefined
  );
  return presentIndicators.length / structureIndicators.length;
}

function calculatePerformanceRelevance(context: AnalysisContext, data: ProcessedData): number {
  // Performance-specific relevance factors
  const performanceIndicators = ['goals', 'metrics', 'evaluations', 'feedback', 'development'];
  const presentIndicators = performanceIndicators.filter(indicator => 
