// Helper functions
function assessFrameworkAlignment(framework: string, data: ProcessedData, domain: string): number {
  // Framework-specific alignment assessment
  const frameworkRequirements = getFrameworkRequirements(framework, domain);
  const dataKeys = Object.keys(data.structured);
  const alignedKeys = frameworkRequirements.filter(req => dataKeys.includes(req));
  return alignedKeys.length / frameworkRequirements.length;
}

function getFrameworkRequirements(framework: string, domain: string): string[] {
  // Return expected data keys for specific frameworks
  const requirements: Record<string, string[]> = {
    'culture_survey': ['values', 'behaviors', 'communication', 'satisfaction'],
    'skills_matrix': ['technical_skills', 'soft_skills', 'competency_levels'],
    'org_chart': ['hierarchy', 'reporting_lines', 'role_definitions'],
    'performance_review': ['goals', 'ratings', 'feedback', 'development_plans'],
    'job_analysis': ['requirements', 'qualifications', 'responsibilities']
  };

  return requirements[framework] || [];
}

function calculateSimilarity(value1: any, value2: any): number {
  // Simple similarity calculation - can be enhanced based on data types
  if (typeof value1 === 'number' && typeof value2 === 'number') {
    const diff = Math.abs(value1 - value2);
    const max = Math.max(Math.abs(value1), Math.abs(value2));
    return max > 0 ? 1 - (diff / max) : 1;
  }

  if (typeof value1 === 'string' && typeof value2 === 'string') {
    return value1.toLowerCase() === value2.toLowerCase() ? 1 : 0;
  }

  return 0.5; // Default similarity for complex types
}

function extractSampleSize(structuredData: Record<string, any>): number {
  // Extract sample size from various possible locations in structured data
  return structuredData.sample_size || 
         structuredData.respondents || 
         structuredData.participants || 
         (Array.isArray(structuredData.data) ? structuredData.data.length : 0) ||
         0;
}

function getExpectedDepthForDomain(domain: string): number {
  // Expected data structure depth for each domain
  const expectedDepths: Record<string, number> = {
    culture: 8,
    skills: 10,
    structure: 6,
    performance: 12,
    hiring: 8
  };

  return expectedDepths[domain] || 8;
}

