/**
 * Validates Mizan business workflows and agent triggering mechanisms
 * Ensures Three-Engine Architecture compliance and module integration integrity
 * 
 * @param context - The loaded Mizan context to validate
 * @throws {Error} If business workflow validation fails
 */
async function validateMizanBusinessWorkflows(context: MizanAgentContext): Promise<void> {
  // Validate Three-Engine Architecture presence
  const requiredEngines = ['KnowledgeEngine.ts', 'DataEngine.ts', 'ReasoningEngine.ts'];
  for (const engine of requiredEngines) {
    if (!context.architecture.threeEngine.knowledgeEngine.includes('KnowledgeEngine.ts') &&
        !context.architecture.threeEngine.dataEngine.includes('DataEngine.ts') &&
        !context.architecture.threeEngine.reasoningEngine.includes('ReasoningEngine.ts')) {
      throw new Error(`Missing required Three-Engine Architecture component: ${engine}`);
    }
  }

  // Validate agent triggering mechanisms
  const criticalTriggers = [
    context.architecture.agentTriggering.cultureToRecognition,
    context.architecture.agentTriggering.cultureToEngagement,
    context.architecture.agentTriggering.skillsToLXP,
  ];

  if (criticalTriggers.some(trigger => !trigger)) {
    throw new Error('Critical Mizan agent triggering mechanisms are disabled');
  }

  // Validate tenant isolation requirements
  if (!context.tenantConfig.tenantIdRequired) {
    throw new Error('Mizan requires tenant isolation - tenantIdRequired must be true');
  }

  // Validate quality rules compliance
  const qualityChecks = [
    context.qualityRules.strictTypeScript,
    context.qualityRules.tenantIsolation,
    context.qualityRules.productionReady,
    context.qualityRules.noMockData,
  ];

  if (qualityChecks.some(check => !check)) {
    throw new Error('Mizan quality rules validation failed - all quality rules must be enabled');
  }
}

/**
 * Gets specific module configuration for agent operations
 * Maintains business workflow integrity and cross-module dependencies
 * 
 * @param moduleName - The Mizan module to get configuration for
 * @param context - The loaded Mizan context
 * @returns Module-specific configuration with type safety
 */
export function getMizanModuleConfig(
  moduleName: string,
  context: MizanAgentContext
): {
  isCore: boolean;
  isBusiness: boolean;
  isTriggered: boolean;
  isAgent: boolean;
  dependencies: string[];
  triggers: string[];
} {
  const isCore = context.modules.core.includes(moduleName as any);
  const isBusiness = context.modules.business.includes(moduleName as any);
  const isTriggered = context.modules.triggered.includes(moduleName as any);
  const isAgent = context.modules.agents.includes(moduleName as any);

  // Define module-specific dependencies based on Mizan architecture
  const dependencies: string[] = [];
  const triggers: string[] = [];

  if (moduleName === 'performance') {
    dependencies.push('structure', 'culture', 'skills');
    triggers.push('talent', 'bonus');
  } else if (moduleName === 'skills') {
    triggers.push('lxp');
  } else if (moduleName === 'culture') {
    triggers.push('recognition', 'engagement');
  } else if (moduleName === 'structure') {
    triggers.push('hiring');
  }

  return {
    isCore,
    isBusiness,
    isTriggered,
    isAgent,
    dependencies,
    triggers,
  };
}

