}

/**
 * Extracts code from AI agent response with strict type safety
 * Follows Mizan Three-Engine Architecture pattern for data processing
 * @param response - Structured AI agent response
 * @returns Extracted and validated code with metadata
 */
function extractCodeFromResponse(response: AIAgentResponse): ExtractedCode {
  try {
    // Data Engine: Process and validate response structure
    if (!response || typeof response !== 'object') {
      throw new Error('Invalid response structure: Expected AIAgentResponse object');
    }

    // Knowledge Engine: Apply extraction rules and patterns
    const codePatterns = [
      /```(\w+)?\n([\s\S]*?)```/g,
      /<code[^>]*>([\s\S]*?)<\/code>/g,
      /`([^`]+)`/g
    ];

    let extractedCode = '';
    let language = 'typescript'; // Default for Mizan platform
    let confidence = 0;

    // Reasoning Engine: Analyze and extract optimal code
    if (response.codeBlocks && response.codeBlocks.length > 0) {
      // Prefer structured code blocks
      const primaryBlock = response.codeBlocks[0];
      extractedCode = primaryBlock.code;
      language = primaryBlock.language || 'typescript';
      confidence = 0.95;
    } else if (response.content) {
      // Fallback to pattern matching
      for (const pattern of codePatterns) {
        const matches = Array.from(response.content.matchAll(pattern));
        if (matches.length > 0) {
          const match = matches[0];
          extractedCode = match[2] || match[1] || '';
          language = match[1] || 'typescript';
          confidence = 0.8;
          break;
        }
      }
    }

    // Validate Mizan compliance patterns
    const mizanCompliant = validateMizanCompliance(extractedCode);
    const isComplete = extractedCode.length > 0 && !extractedCode.includes('TODO') && !extractedCode.includes('PLACEHOLDER');

    return {
      code: extractedCode.trim(),
      language,
      confidence,
      isComplete,
      mizanCompliant
    };

  } catch (error) {
    console.error('Code extraction error:', error);
    return {
      code: '',
      language: 'typescript',
      confidence: 0,
      isComplete: false,
      mizanCompliant: false
    };
  }
}

/**
 * Validates extracted code against Mizan platform requirements
 * @param code - Code to validate
 * @returns Boolean indicating Mizan compliance
 */
function validateMizanCompliance(code: string): boolean {
  const mizanPatterns = {
    tenantIsolation: /tenantId/,
    drizzleORM: /from\s+['"]drizzle-orm/,
    nextjsAppRouter: /from\s+['"]next\/app/,
    strictTypes: /:\s*\w+(\[\])?(\s*\|\s*\w+(\[\])?)*\s*[=;]/,
    noAnyTypes: /:\s*any\b/,
    errorHandling: /try\s*{[\s\S]*catch/
  };

  const compliance = {
    hasTenantIsolation: mizanPatterns.tenantIsolation.test(code),
    usesDrizzleORM: mizanPatterns.drizzleORM.test(code) || !code.includes('prisma'),
    usesAppRouter: mizanPatterns.nextjsAppRouter.test(code) || !code.includes('getServerSideProps'),
    hasStrictTypes: mizanPatterns.strictTypes.test(code),
    noAnyTypes: !mizanPatterns.noAnyTypes.test(code),
    hasErrorHandling: mizanPatterns.errorHandling.test(code) || code.length < 100 // Small utilities may not need try-catch
  };

  // Return true if most patterns are satisfied
  const score = Object.values(compliance).filter(Boolean).length;
  return score >= 4; // At least 4 out of 6 criteria met
}

