 * 
 * @param validation - Complete validation result with Mizan-specific analysis
 * @param previousViolations - Historical violation data for pattern analysis
 * @returns Comprehensive refinement instructions for Mizan compliance
 */
function buildRefinementInstructions(
  validation: ValidationResult,
  previousViolations: PreviousViolation[]
): RefinementInstructions {
  // Analyze violation patterns and Mizan-specific context
  const violationPatterns = analyzeViolationPatterns(previousViolations);
  const mizanContext = extractMizanContext(validation);
  const businessCriticality = assessBusinessCriticality(validation.businessImpact);

  // Determine refinement priority based on Mizan business impact
  const priority = determinePriority(validation, businessCriticality);

  // Select appropriate approach based on architectural compliance
  const approach = selectApproach(validation.architecturalCompliance, validation.fixStrategy);

  // Generate focus areas specific to Mizan platform requirements
  const focusAreas = generateFocusAreas(validation, mizanContext);

  // Create Mizan-specific guidance
  const mizanSpecificGuidance: MizanGuidance = {
    architecturalPatterns: generateArchitecturalPatterns(validation),
    moduleIntegrations: identifyModuleIntegrations(validation),
    businessWorkflows: mapBusinessWorkflows(validation.businessImpact),
    tenantIsolationRequirements: generateTenantRequirements(validation),
    agentTriggeringRules: defineAgentTriggeringRules(validation)
  };

  // Create comprehensive testing strategy
  const testingStrategy: TestingStrategy = {
    unitTests: generateUnitTests(validation),
    integrationTests: generateIntegrationTests(validation),
    businessWorkflowTests: generateBusinessWorkflowTests(validation.businessImpact),
    tenantIsolationTests: generateTenantIsolationTests(validation),
    agentTriggeringTests: generateAgentTriggeringTests(validation)
  };

  // Create rollback plan for business continuity
  const rollbackPlan: RollbackPlan = {
    backupRequired: assessBackupRequirement(validation),
    rollbackSteps: generateRollbackSteps(validation),
    dataIntegrityChecks: generateDataIntegrityChecks(validation),
    businessContinuityMeasures: generateContinuityMeasures(validation.businessImpact)
  };

  return {
    priority,
    approach,
    focusAreas,
    mizanSpecificGuidance,
    testingStrategy,
    rollbackPlan
  };
}

/**
 * Analyzes patterns in previous violations to inform refinement strategy
 */
function analyzeViolationPatterns(previousViolations: PreviousViolation[]): {
  recurringRules: string[];
  problematicModules: string[];
  fixSuccessRate: number;
} {
  const ruleFrequency = new Map<string, number>();
  const moduleFrequency = new Map<string, number>();
  let totalFixes = 0;
  let successfulFixes = 0;

  previousViolations.forEach(violation => {
    ruleFrequency.set(violation.rule, (ruleFrequency.get(violation.rule) || 0) + 1);

    if (violation.mizanContext?.module) {
      moduleFrequency.set(
        violation.mizanContext.module,
        (moduleFrequency.get(violation.mizanContext.module) || 0) + 1
      );
    }

    totalFixes += violation.fixAttempts;
    if (violation.status === 'fixed') {
      successfulFixes++;
    }
  });

  return {
    recurringRules: Array.from(ruleFrequency.entries())
      .sort(([,a], [,b]) => b - a)
      .slice(0, 5)
      .map(([rule]) => rule),
    problematicModules: Array.from(moduleFrequency.entries())
      .sort(([,a], [,b]) => b - a)
      .slice(0, 3)
      .map(([module]) => module),
    fixSuccessRate: totalFixes > 0 ? successfulFixes / totalFixes : 0
  };
}

/**
 * Extracts Mizan-specific context from validation results
 */
function extractMizanContext(validation: ValidationResult): {
  affectedEngines: string[];
  moduleIntegrations: string[];
  workflowDependencies: string[];
} {
  const affectedEngines: string[] = [];

  // Determine which Three-Engine components are affected
  if (!validation.architecturalCompliance.threeEnginePattern) {
    affectedEngines.push('KnowledgeEngine', 'DataEngine', 'ReasoningEngine');
  }

  return {
    affectedEngines,
    moduleIntegrations: validation.businessImpact.affectedModules,
    workflowDependencies: validation.businessImpact.workflowsBreaking
  };
}

/**
 * Assesses business criticality based on impact analysis
 */
function assessBusinessCriticality(businessImpact: BusinessImpact): 'low' | 'medium' | 'high' | 'critical' {
  // Critical: Core business workflows broken
  if (businessImpact.workflowsBreaking.some(workflow => 
    workflow.includes('CultureRecognition') || 
    workflow.includes('SkillsLXP') ||
    workflow.includes('PerformanceTalent')
  )) {
    return 'critical';
  }

  // High: Multiple modules affected
  if (businessImpact.affectedModules.length > 2) {
    return 'high';
  }

  // Medium: Single critical module affected
  if (businessImpact.affectedModules.some(module => 
    ['Culture', 'Skills', 'Performance', 'Hiring'].includes(module)
  )) {
    return 'medium';
  }

  return 'low';
}

/**
 * Determines refinement priority based on validation and business criticality
 */
function determinePriority(
  validation: ValidationResult,
  businessCriticality: 'low' | 'medium' | 'high' | 'critical'
): 'low' | 'medium' | 'high' | 'critical' {
  // Override with business criticality if higher
  const technicalPriority = validation.technicalAssessment.securityRisk === 'critical' ? 'critical' :
    validation.technicalAssessment.securityRisk === 'high' ? 'high' :
    validation.technicalAssessment.productionReadiness ? 'medium' : 'low';

  return businessCriticality > technicalPriority ? businessCriticality : technicalPriority;
}

/**
 * Selects appropriate refinement approach based on compliance and strategy
 */
function selectApproach(
  architecturalCompliance: ArchitecturalCompliance,
  fixStrategy: FixStrategy
): 'incremental' | 'comprehensive' | 'architectural' {
  // Architectural approach for major compliance issues
  if (!architecturalCompliance.threeEnginePattern || 
      !architecturalCompliance.moduleIntegration ||
      !architecturalCompliance.tenantIsolation) {
    return 'architectural';
  }

  // Comprehensive approach for complex fixes
  if (fixStrategy.complexity === 'complex' || fixStrategy.complexity === 'enterprise') {
    return 'comprehensive';
  }

  // Incremental approach for simple fixes
  return 'incremental';
}

