 * Enhances prompts while maintaining Mizan platform architecture integrity
 * Ensures business workflow completeness and strategic alignment
 */
function enhancePromptWithViolations(
  originalPrompt: string,
  refinementInstructions: MizanRefinementInstructions,
  previousCode: MizanCodeContext,
  attempt: number
): MizanPromptEnhancement {
  try {
    // Validate inputs with Mizan schemas
    const validatedRefinement = mizanRefinementSchema.parse(refinementInstructions);
    const validatedCode = mizanCodeContextSchema.parse(previousCode);

    // Mizan Architecture Analysis
    const mizanArchitectureContext = analyzeMizanArchitecture(validatedCode);

    // Business Logic Preservation Check
    const businessLogicContext = analyzeMizanBusinessLogic(validatedCode);

    // Generate Mizan-compliant enhancement
    const enhancedPrompt = generateMizanCompliantPrompt(
      originalPrompt,
      validatedRefinement,
      validatedCode,
      mizanArchitectureContext,
      businessLogicContext,
      attempt
    );

    // Validate enhancement quality
    const enhancement: MizanPromptEnhancement = {
      enhancedPrompt,
      confidence: calculateMizanConfidence(enhancedPrompt, validatedRefinement),
      mizanCompliance: validateMizanCompliance(enhancedPrompt),
      businessLogicPreserved: validateBusinessLogicPreservation(enhancedPrompt, businessLogicContext),
      architectureIntegrity: validateArchitectureIntegrity(enhancedPrompt, mizanArchitectureContext)
    };

    return enhancement;
  } catch (error) {
    console.error('Mizan Prompt Enhancement Error:', error);
    throw new Error(`Failed to enhance prompt with Mizan compliance: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

/**
 * Analyzes Mizan architecture patterns in code context
 */
function analyzeMizanArchitecture(codeContext: MizanCodeContext): {
  hasThreeEnginePattern: boolean;
  hasAgentTriggering: boolean;
  hasModuleIntegration: boolean;
  hasTenantIsolation: boolean;
  moduleType: string | null;
} {
  const code = codeContext.content.toLowerCase();

  return {
    hasThreeEnginePattern: code.includes('knowledgeengine') && code.includes('dataengine') && code.includes('reasoningengine'),
    hasAgentTriggering: code.includes('trigger') && (code.includes('recognition') || code.includes('engagement') || code.includes('lxp')),
    hasModuleIntegration: code.includes('culture') || code.includes('skills') || code.includes('performance') || code.includes('hiring'),
    hasTenantIsolation: code.includes('tenantid') && code.includes('where'),
    moduleType: detectMizanModule(codeContext.filePath)
  };
}

/**
 * Analyzes business logic patterns in Mizan code
 */
function analyzeMizanBusinessLogic(codeContext: MizanCodeContext): {
  hasWorkflowCompleteness: boolean;
  hasStrategicAlignment: boolean;
  hasCrossModuleDependencies: boolean;
  businessRisk: 'low' | 'medium' | 'high';
} {
  const code = codeContext.content.toLowerCase();

  const hasWorkflowCompleteness = code.includes('workflow') || code.includes('process') || code.includes('complete');
  const hasStrategicAlignment = code.includes('strategy') || code.includes('strategic') || code.includes('alignment');
  const hasCrossModuleDependencies = code.includes('integration') || code.includes('trigger') || code.includes('dependency');

  let businessRisk: 'low' | 'medium' | 'high' = 'low';
  if (codeContext.filePath.includes('agents/') || codeContext.filePath.includes('services/')) {
    businessRisk = 'high';
  } else if (codeContext.filePath.includes('api/') || codeContext.filePath.includes('components/')) {
    businessRisk = 'medium';
  }

  return {
    hasWorkflowCompleteness,
    hasStrategicAlignment,
    hasCrossModuleDependencies,
    businessRisk
  };
}

