/**
 * Calculates confidence score for Mizan enhancement
 */
function calculateMizanConfidence(
  enhancedPrompt: string,
  refinement: MizanRefinementInstructions
): number {
  let confidence = 0.7; // Base confidence

  // Boost confidence based on Mizan-specific factors
  if (enhancedPrompt.includes('Three-Engine Architecture')) confidence += 0.1;
  if (enhancedPrompt.includes('tenantId')) confidence += 0.1;
  if (enhancedPrompt.includes('Drizzle ORM')) confidence += 0.05;
  if (enhancedPrompt.includes('production-ready')) confidence += 0.05;

  // Adjust based on complexity
  switch (refinement.complexity) {
    case 'simple': confidence += 0.1; break;
    case 'moderate': confidence += 0.05; break;
    case 'complex': confidence -= 0.05; break;
    case 'enterprise': confidence -= 0.1; break;
  }

  return Math.min(confidence, 1.0);
}

