 */
function detectMizanModule(filePath: string): string | null {
  const modules = ['structure', 'culture', 'skills', 'performance', 'hiring', 'lxp', 'talent', 'bonus'];

  for (const module of modules) {
    if (filePath.toLowerCase().includes(module)) {
      return module;
