}

// Zod schema for fix plan validation
const FixPlanSchema = z.object({
  id: z.string(),
  file: z.string(),
  violation: z.object({
    rule: z.string(),
    line: z.number(),
    code: z.string(),
    severity: z.enum(['low', 'medium', 'high', 'critical'])
  }),
  mizanContext: z.object({
    affectedModules: z.array(z.string()),
    workflowsImpacted: z.array(z.string()),
    architectureCompliance: z.object({
      threeEnginePattern: z.boolean(),
      agentTriggering: z.boolean(),
      moduleIntegration: z.boolean(),
      tenantIsolation: z.boolean()
    }),
    businessRisk: z.string()
  }),
  fix: z.object({
    approach: z.string(),
    complexity: z.enum(['simple', 'moderate', 'complex', 'enterprise']),
    estimatedEffort: z.enum(['small', 'medium', 'large', 'enterprise']),
    migrationRequired: z.boolean(),
    testingRequired: z.array(z.string())
  }),
  recommendation: z.enum(['PROCEED', 'REVIEW', 'REJECT']),
  priority: z.number().min(1).max(10)
});

type FixPlan = z.infer<typeof FixPlanSchema>;

// Mizan-specific error classes
class MizanFixPlanError extends Error {
  constructor(message: string, public readonly code: string) {
    super(message);
    this.name = 'MizanFixPlanError';
  }
}

class MizanValidationError extends MizanFixPlanError {
  constructor(message: string, public readonly validationErrors: z.ZodError) {
    super(message, 'VALIDATION_ERROR');
  }
}

// Production-ready fix plan parser with comprehensive error handling
function parseFixPlan(fixPlanContent: unknown): FixPlan {
  try {
    // Validate input is not null/undefined
    if (!fixPlanContent) {
      throw new MizanFixPlanError(
        'Fix plan content cannot be null or undefined',
        'NULL_INPUT'
      );
    }

    // Handle string input (JSON parsing)
    let parsedContent: unknown;
    if (typeof fixPlanContent === 'string') {
      try {
        parsedContent = JSON.parse(fixPlanContent);
      } catch (parseError) {
        throw new MizanFixPlanError(
          `Invalid JSON in fix plan content: ${parseError instanceof Error ? parseError.message : 'Unknown error'}`,
          'JSON_PARSE_ERROR'
        );
      }
    } else {
      parsedContent = fixPlanContent;
    }

    // Validate against Mizan fix plan schema
    const validationResult = FixPlanSchema.safeParse(parsedContent);

    if (!validationResult.success) {
      throw new MizanValidationError(
        'Fix plan content does not match required Mizan schema',
        validationResult.error
      );
    }

    const fixPlan = validationResult.data;

    // Mizan-specific business logic validation
    validateMizanBusinessLogic(fixPlan);

    // Log successful parsing for audit trail
    console.log(`[Mizan Agent] Successfully parsed fix plan for ${fixPlan.file} - Priority: ${fixPlan.priority}`);

    return fixPlan;

  } catch (error) {
    // Comprehensive error logging for debugging
    console.error('[Mizan Agent] Fix plan parsing failed:', {
      error: error instanceof Error ? error.message : 'Unknown error',
      code: error instanceof MizanFixPlanError ? error.code : 'UNKNOWN_ERROR',
      timestamp: new Date().toISOString(),
      inputType: typeof fixPlanContent,
      inputLength: typeof fixPlanContent === 'string' ? fixPlanContent.length : 'N/A'
    });

    // Re-throw with context
    if (error instanceof MizanFixPlanError) {
      throw error;
    }

    throw new MizanFixPlanError(
      `Unexpected error during fix plan parsing: ${error instanceof Error ? error.message : 'Unknown error'}`,
      'UNEXPECTED_ERROR'
    );
  }
}

// Mizan business logic validation
function validateMizanBusinessLogic(fixPlan: FixPlan): void {
  // Validate Mizan module references
  const validMizanModules = [
    'Culture', 'Skills', 'Structure', 'Performance', 
    'Hiring', 'LXP', 'Talent', 'Bonus', 'Recognition', 'Engagement'
  ];

  const invalidModules = fixPlan.mizanContext.affectedModules.filter(
    module => !validMizanModules.includes(module)
  );

  if (invalidModules.length > 0) {
    throw new MizanFixPlanError(
      `Invalid Mizan modules referenced: ${invalidModules.join(', ')}`,
      'INVALID_MODULES'
    );
  }

  // Validate workflow integrity
  const criticalWorkflows = [
    'CultureRecognition', 'CultureEngagement', 'SkillsLXP',
    'StructureHiring', 'PerformanceTalent', 'PerformanceBonus'
  ];

  const hasWorkflowImpact = fixPlan.mizanContext.workflowsImpacted.some(
    workflow => criticalWorkflows.includes(workflow)
  );

  if (hasWorkflowImpact && fixPlan.recommendation !== 'PROCEED') {
    console.warn('[Mizan Agent] Critical workflow impact detected but recommendation is not PROCEED');
  }

  // Validate tenant isolation requirements
  if (fixPlan.file.includes('backend/') && !fixPlan.mizanContext.architectureCompliance.tenantIsolation) {
    throw new MizanFixPlanError(
      'Backend fixes must maintain tenant isolation compliance',
      'TENANT_ISOLATION_REQUIRED'
    );
  }

  // Validate Three-Engine Architecture for AI modules
  const aiModules = ['Culture', 'Skills', 'Structure', 'Performance', 'Hiring'];
  const affectsAIModule = fixPlan.mizanContext.affectedModules.some(
    module => aiModules.includes(module)
  );

  if (affectsAIModule && !fixPlan.mizanContext.architectureCompliance.threeEnginePattern) {
    console.warn('[Mizan Agent] AI module affected but Three-Engine pattern compliance not verified');
  }
}

// Enhanced error handling for agent workflow
function handleFixPlanError(error: unknown, context: string): never {
  if (error instanceof MizanValidationError) {
    console.error(`[Mizan Agent] Validation error in ${context}:`, {
      message: error.message,
      validationErrors: error.validationErrors.errors,
      timestamp: new Date().toISOString()
    });
  } else if (error instanceof MizanFixPlanError) {
    console.error(`[Mizan Agent] Fix plan error in ${context}:`, {
      message: error.message,
      code: error.code,
      timestamp: new Date().toISOString()
    });
  } else {
    console.error(`[Mizan Agent] Unexpected error in ${context}:`, {
      error: error instanceof Error ? error.message : 'Unknown error',
      timestamp: new Date().toISOString()
    });
  }

  throw error;
}

