// Mizan-compliant TypeScript implementation with strict types
interface IssueTypeAnalysis {
  type: 'architecture' | 'business_logic' | 'integration' | 'tenant_isolation' | 'security' | 'performance' | 'ui_ux';
  severity: 'critical' | 'high' | 'medium' | 'low';
  mizanModule: 'culture' | 'skills' | 'structure' | 'performance' | 'hiring' | 'lxp' | 'talent' | 'bonus' | 'core' | 'agent_system';
  workflowImpact: string[];
  agentTriggering: boolean;
  tenantIsolationRequired: boolean;
}

function determineIssueType(description: string, phaseName: string): IssueTypeAnalysis {
  // Mizan-specific issue classification with business context
  const lowerDesc = description.toLowerCase();
  const lowerPhase = phaseName.toLowerCase();

  // Critical Mizan architecture patterns
  if (lowerDesc.includes('tenant') || lowerDesc.includes('isolation')) {
    return {
      type: 'tenant_isolation',
      severity: 'critical',
      mizanModule: 'core',
      workflowImpact: ['all_modules'],
      agentTriggering: false,
      tenantIsolationRequired: true
    };
  }

  // Three-Engine Architecture violations
  if (lowerDesc.includes('knowledge engine') || lowerDesc.includes('data engine') || lowerDesc.includes('reasoning engine')) {
    return {
      type: 'architecture',
      severity: 'high',
      mizanModule: 'core',
      workflowImpact: ['analysis_workflows'],
      agentTriggering: true,
      tenantIsolationRequired: true
    };
  }

  // Culture module and agent triggering
  if (lowerDesc.includes('culture') || lowerDesc.includes('recognition') || lowerDesc.includes('engagement')) {
    return {
      type: 'business_logic',
      severity: 'high',
      mizanModule: 'culture',
      workflowImpact: ['culture_analysis', 'recognition_agent', 'engagement_agent'],
      agentTriggering: true,
      tenantIsolationRequired: true
    };
  }

  // Skills module and LXP triggering
  if (lowerDesc.includes('skills') || lowerDesc.includes('lxp') || lowerDesc.includes('learning')) {
    return {
      type: 'business_logic',
      severity: 'high',
      mizanModule: 'skills',
      workflowImpact: ['skills_analysis', 'lxp_triggering', 'learning_paths'],
      agentTriggering: true,
      tenantIsolationRequired: true
    };
  }

  // Performance module integration
  if (lowerDesc.includes('performance') || lowerDesc.includes('goals') || lowerDesc.includes('evaluation')) {
    return {
      type: 'business_logic',
      severity: 'high',
      mizanModule: 'performance',
      workflowImpact: ['performance_management', 'goal_setting', 'evaluations'],
      agentTriggering: false,
      tenantIsolationRequired: true
    };
  }

  // Database and ORM issues
  if (lowerDesc.includes('drizzle') || lowerDesc.includes('database') || lowerDesc.includes('query')) {
    return {
      type: 'integration',
      severity: 'high',
      mizanModule: 'core',
      workflowImpact: ['data_persistence', 'multi_tenant_queries'],
      agentTriggering: false,
      tenantIsolationRequired: true
    };
  }

  // TypeScript and code quality
  if (lowerDesc.includes('typescript') || lowerDesc.includes('types') || lowerDesc.includes('any')) {
    return {
      type: 'architecture',
      severity: 'medium',
      mizanModule: 'core',
      workflowImpact: ['code_quality', 'maintainability'],
      agentTriggering: false,
      tenantIsolationRequired: false
    };
  }

  // Default classification for unknown issues
  return {
    type: 'architecture',
    severity: 'medium',
    mizanModule: 'core',
    workflowImpact: ['general_platform'],
    agentTriggering: false,
    tenantIsolationRequired: false
  };
}

