          strategicPriorities: data.strategicPriorities,
          behaviorChangeTargets: data.behaviorChangeTargets
        },
        status: 'pending',
        createdAt: new Date(),
        processedAt: null
      });
    }

    return triggers;
  }
}

// Main Developer Agent Class with Three-Engine Architecture
class MizanDeveloperAgent {
  private knowledgeEngine: DeveloperAgentKnowledgeEngine;
  private dataEngine: DeveloperAgentDataEngine;
  private reasoningEngine: DeveloperAgentReasoningEngine;

  constructor() {
    this.knowledgeEngine = new DeveloperAgentKnowledgeEngine();
    this.dataEngine = new DeveloperAgentDataEngine();
    this.reasoningEngine = new DeveloperAgentReasoningEngine();
  }

  async executeAgent(
    agentType: MizanAgent['type'],
    tenantId: string,
    inputData: Record<string, unknown>
  ): Promise<AgentExecutionResult> {
    try {
      // Validate tenant isolation
      if (!tenantId || typeof tenantId !== 'string') {
        throw new Error('Invalid tenantId: Multi-tenant isolation required');
      }

      // Three-Engine Architecture execution
      const context = await this.knowledgeEngine.getAgentContext(agentType);
      const processedData = await this.dataEngine.processAgentData(inputData, context, tenantId);
      const result = await this.reasoningEngine.analyzeAgentExecution(processedData, context, tenantId);

      return result;
    } catch (error) {
      return {
        agentId: randomUUID(),
        tenantId,
        executionTime: Date.now(),
        status: 'error',
        results: {},
        errors: [error instanceof Error ? error.message : 'Unknown error'],
        triggersCreated: []
      };
    }
  }
}

// Production-Ready Main Function with Strict TypeScript Types
async function runDeveloperAgent(
  config: DeveloperAgentConfig
): Promise<DeveloperAgentResponse> {
  try {
    // Validate configuration with Zod schema
    const validatedConfig = DeveloperAgentConfigSchema.parse(config);

    const executionId = randomUUID();
    const startTime = Date.now();
    const agent = new MizanDeveloperAgent();
    const executionResults: AgentExecutionResult[] = [];
    let totalTriggers = 0;

    // Execute each agent type with tenant isolation
    for (const agentType of validatedConfig.agentTypes) {
      const inputData = {
        tenantId: validatedConfig.tenantId,
        agentType,
        executionId,
        timestamp: new Date().toISOString(),
        debugMode: validatedConfig.debugMode
      };

      const result = await agent.executeAgent(agentType, validatedConfig.tenantId, inputData);
      executionResults.push(result);
      totalTriggers += result.triggersCreated.length;
    }

    const totalExecutionTime = Date.now() - startTime;
    const successfulAgents = executionResults.filter(r => r.status === 'success').length;
    const failedAgents = executionResults.filter(r => r.status === 'error').length;

    const response: DeveloperAgentResponse = {
      executionId,
      tenantId: validatedConfig.tenantId,
      totalAgents: validatedConfig.agentTypes.length,
      successfulAgents,
      failedAgents,
      executionResults,
      totalExecutionTime,
      triggersGenerated: totalTriggers,
      status: failedAgents === 0 ? 'completed' : (successfulAgents > 0 ? 'partial' : 'failed')
    };

    return response;
  } catch (error) {
    throw new Error(`Developer Agent execution failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

