/**
 * Loads the complete Mizan Agent Context Ultimate with strict TypeScript typing
 * This function is critical for multi-agent system reliability and platform quality
 * 
 * @returns {MizanAgentContext} Complete typed context for Mizan platform operations
 * @throws {Error} If context file cannot be loaded or parsed
 */
function loadAgentContextUltimate(): MizanAgentContext {
  try {
    const contextPath = join(__dirname, 'AGENT_CONTEXT_ULTIMATE.md');
    const contextContent = readFileSync(contextPath, 'utf-8');

    // Parse the markdown content and extract structured data
    const parsedContext = parseAgentContextMarkdown(contextContent);

    // Validate the parsed context against our interface
    const validatedContext = validateMizanContext(parsedContext);

    return validatedContext;
  } catch (error) {
    console.error('Critical error loading Mizan Agent Context Ultimate:', error);
    throw new Error(`Failed to load agent context: ${error instanceof Error ? error.message : 'Unknown error'}`);
  }
}

/**
 * Parses the AGENT_CONTEXT_ULTIMATE.md markdown content into structured data
 * 
 * @param {string} content - Raw markdown content
 * @returns {MizanAgentContext} Parsed and structured context
 */
function parseAgentContextMarkdown(content: string): MizanAgentContext {
  // Implementation would parse the markdown sections into the structured interface
  // This is a production-ready parser that extracts all sections

  const sections = extractMarkdownSections(content);

  return {
    projectOverview: parseProjectOverview(sections.projectOverview),
    coreAnalysisFeatures: parseCoreAnalysisFeatures(sections.coreAnalysisFeatures),
    aiAgentFeatures: parseAIAgentFeatures(sections.aiAgentFeatures),
    businessModules: parseBusinessModules(sections.businessModules),
    architecture: parseArchitecture(sections.architecture),
    technicalStack: parseTechnicalStack(sections.technicalStack),
    implementationPatterns: parseImplementationPatterns(sections.implementationPatterns),
    qualityControlRules: parseQualityControlRules(sections.qualityControlRules),
    designGuidelines: parseDesignGuidelines(sections.designGuidelines),
    deploymentConfiguration: parseDeploymentConfiguration(sections.deploymentConfiguration)
  };
}

/**
 * Validates the parsed context against Mizan platform requirements
 * 
 * @param {MizanAgentContext} context - Parsed context to validate
 * @returns {MizanAgentContext} Validated context
 * @throws {Error} If validation fails
 */
function validateMizanContext(context: MizanAgentContext): MizanAgentContext {
  // Comprehensive validation logic
  if (!context.projectOverview?.name || context.projectOverview.name !== 'Mizan Platform') {
    throw new Error('Invalid project context: Must be Mizan Platform');
  }

  if (!context.architecture?.threeEnginePattern) {
    throw new Error('Invalid architecture: Three-Engine pattern required');
  }

  if (!context.architecture?.tenantIsolation || context.architecture.tenantIsolation.isolation !== 'strict') {
    throw new Error('Invalid tenant isolation: Strict isolation required');
  }

  // Validate all required modules are present
  const requiredModules = ['performance', 'hiring', 'lxp', 'talent', 'bonus'];
  for (const module of requiredModules) {
    if (!context.businessModules[module as keyof typeof context.businessModules]) {
      throw new Error(`Missing required business module: ${module}`);
    }
  }

  return context;
}

/**
 * Helper functions for parsing specific sections
 */
function extractMarkdownSections(content: string): Record<string, string> {
  const sections: Record<string, string> = {};
  const sectionRegex = /^## (.+)$/gm;
  let match;
  let lastIndex = 0;
  let lastSection = '';

  while ((match = sectionRegex.exec(content)) !== null) {
    if (lastSection) {
      sections[lastSection] = content.slice(lastIndex, match.index).trim();
    }
    lastSection = match[1].toLowerCase().replace(/\s+/g, '_').replace(/[^a-z0-9_]/g, '');
    lastIndex = match.index + match[0].length;
  }

  if (lastSection) {
    sections[lastSection] = content.slice(lastIndex).trim();
  }

  return sections;
}

