/**
 * Extracts triggering patterns from Mizan agent context
 * Supports Three-Engine Architecture and cross-module workflows
 * @param {MizanAgentContext} agentContext - Complete agent context with tenant isolation
 * @returns {MizanTriggeringPatterns} Structured triggering patterns for module activation
 */
function extractTriggeringPatterns(agentContext) {
  // Validate required context structure for Mizan compliance
  if (!agentContext || typeof agentContext !== 'object') {
    throw new Error('Invalid agent context: must be a valid MizanAgentContext object');
  }

  // Ensure tenant isolation is maintained
  if (!agentContext.tenantId || typeof agentContext.tenantId !== 'string') {
    throw new Error('Missing tenantId: all Mizan operations require tenant isolation');
  }

  // Extract Three-Engine Architecture patterns
  const knowledgeEnginePatterns = extractKnowledgePatterns(agentContext.knowledge || {});
  const dataEnginePatterns = extractDataPatterns(agentContext.data || {});
  const reasoningEnginePatterns = extractReasoningPatterns(agentContext.reasoning || {});

  // Extract agent triggering patterns for Culture  Recognition/Engagement
  const agentTriggers = extractAgentTriggers(agentContext.agents || {});

  // Extract module integration patterns for Skills  LXP, Performance  Talent/Bonus
  const moduleIntegrations = extractModuleIntegrations(agentContext.modules || {});

  // Extract workflow dependencies for business logic completeness
  const workflowDependencies = extractWorkflowDependencies(agentContext.workflows || {});

  return {
    tenantId: agentContext.tenantId,
    threeEnginePatterns: {
      knowledge: knowledgeEnginePatterns,
      data: dataEnginePatterns,
      reasoning: reasoningEnginePatterns
    },
    agentTriggers: agentTriggers,
    moduleIntegrations: moduleIntegrations,
    workflowDependencies: workflowDependencies,
    strategicAlignment: agentContext.strategy || {},
    timestamp: new Date().toISOString(),
    version: '1.0.0'
  };
}

